import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Box from "@material-ui/core/Box";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";
import Grid from "@material-ui/core/Grid";
import TextField from "@material-ui/core/TextField";
import Hidden from "@material-ui/core/Hidden";
import IconButton from "@material-ui/core/IconButton";
import ArrowBackIosIcon from "@material-ui/icons/ArrowBackIos";
import CancelIcon from "@material-ui/icons/Cancel";
import CloudUploadOutlinedIcon from "@material-ui/icons/CloudUploadOutlined";

import { Link } from 'react-router-dom';

import DocSideNav from "./DocSideNav";
import Navbar from "../../Components/Navbar";
import CovidAlert from "../../Components/Alert";

import { connect } from "react-redux";
import PropTypes from "prop-types";

import { updateVerification } from "../../redux/actions/userActions";



const useStyles = makeStyles((theme) => ({
  covidBox: {
    marginTop: 64,
    [theme.breakpoints.down("sm")]: {
      marginTop: 20,
      marginLeft: 20,
      marginRight: 20,
    },
  },

  anchor: {
    display: "block",
    paddingTop: 100,
    marginTop: -100,
  },
}));


// doctor account verification
function Verification(props) {
  const classes = useStyles();



  const [medicalNumber, setMedicalNumber] = React.useState();
  const [identityCard, setIdentityCard] = React.useState({
    src: "",
    name: ""
  });
  const [medicalDegree, setMedicalDegree] = React.useState({
    src: "",
    name: ""
  });

  const editMedicalNumber = (event) => {
    setMedicalNumber(event.target.value);
  }

  const uploadIdentityCard = (event) => {
    let reader = new FileReader();
        let uploaded = event.target.files[0];
        if (uploaded) {
            reader.readAsDataURL(uploaded);
        }
        reader.onload = () => {
          setIdentityCard({
              src: reader.result,
              name: uploaded.name
            })
        };
  };

  const uploadMedicalDegree = (event) => {
    let reader = new FileReader();
        let uploaded = event.target.files[0];
        if (uploaded) {
            reader.readAsDataURL(uploaded);
        }
        reader.onload = () => {
          setMedicalDegree({
              src: reader.result,
              name: uploaded.name
            })
        };
  };

  // initiate push to firebase when submit button is clicked
  const submitVerification = (event) => {
    // console.log(props)
    const verificationInfo = {
      medicalNumber: medicalNumber,
      identityCardSrc: medicalNumber.src,
      medicalDegreeSrc: medicalDegree.src,
    };
    props.updateVerification({ verificationInfo });
  };

  return (
    <a id="accountverification" className={classes.anchor}>
      <Grid container spacing={0}>
        <Grid item xs={1}></Grid>
        <Grid item xs={10} md={8}>
          <Box flexGrow={1} flexDirection="row" mb={1}>
            <Typography variant="h5" color="primary">
              {/* Back button 手机屏幕才会出玄 */}
              <Hidden mdUp>
                {/* <IconButton> */}
                <Link to='docaccount'>
                  <ArrowBackIosIcon />
                </Link>
                {/* </IconButton> */}
              </Hidden>
              <strong>Account Verification</strong>
            </Typography>
          </Box>

          <br></br>
          <Box display="flex" flexDirection="row">
            {/* 现在是ACCOUNT NOT VERIFIED, 需要换icon 如果account pending/verified */}
            <CancelIcon style={{ color: "red", marginRight: 10 }} />
            <Typography variant="body1">
              Account not verified<br></br>
            </Typography>
          </Box>
          <br></br>
          <br></br>
          {/* Meidcal reg number */}
          <TextField
            fullWidth
            required
            label="Medical Registration Number"
            variant="outlined"
            onChange={editMedicalNumber}
          />
          {/* Upload malaysian identity card */}
          <Box display="flex" mt={5} mb={3}>
            <Box flexGrow={1}>
              <Typography variant="body1" color="textSecondary">
                Malaysian Identity Card
              </Typography>
            </Box>
            <Box flexGrow={1}>
              <input
                accept="image/*"
                style={{ display: "none" }}
                id="contained-button-identity"
                multiple
                type="file"
                onChange={uploadIdentityCard}
              />
              <label htmlFor="contained-button-identity">
                <Button
                  startIcon={<CloudUploadOutlinedIcon />}
                  variant="outlined"
                  component="span"
                  color="primary"
                  style={{ textTransform: "none", float: "right" }}
                  size="medium"
                >
                  Upload
              </Button>
              </label>
            </Box>
          </Box>
          <Typography variant="body2">{identityCard.name}</Typography>
          {/* Upload medical degree */}
          <Box display="flex" mt={5} mb={5}>
            <Box flexGrow={1}>
              <Typography variant="body1" color="textSecondary">
                Certified copy of Medical Degree
              </Typography>
            </Box>
            <Box>
              <input
                accept="image/*"
                style={{ display: "none" }}
                id="contained-button-medical"
                multiple
                type="file"
                onChange={uploadMedicalDegree}
              />
              <label htmlFor="contained-button-medical">
                <Button
                  startIcon={<CloudUploadOutlinedIcon />}
                  variant="outlined"
                  component="span"
                  color="primary"
                  style={{ textTransform: "none", float: "right" }}
                  size="medium"
                >
                  Upload
              </Button>
              </label>
            </Box>
          </Box>
          <Typography variant="body2">{medicalDegree.name}</Typography>

          <Button
            variant="contained"
            color="primary"
            style={{ textTransform: "none", float: "right" }}
            size="large"
            onClick={submitVerification}
          >
            Submit
          </Button>
          <br></br>
          <br></br>
        </Grid>
        <Grid item xs={1} md={3}></Grid>
      </Grid>
    </a>
  );
}

function DocAccountVerification(props) {
  const classes = useStyles();

  // console.log(props.updateVerification)
  let updateVerification = props.updateVerification;

  // console.log(props.updateVerification);
  console.log(updateVerification);

  return (
    <div>
      <Navbar />
      <Hidden smDown>
        <div className={classes.covidBox} style={{ position: "relative" }}>
          <CovidAlert />

          <Grid container spacing={0}>
            <Grid item md={4} lg={3}>
              <DocSideNav />
            </Grid>

            <Grid item sm={12} md={8} lg={9}>
              <Verification updateVerification={props.updateVerification}/>
              {/* <DocCard /> */}
            </Grid>
          </Grid>
        </div>
      </Hidden>
      <Hidden mdUp>
        <Verification />
      </Hidden>
    </div>
  );
}


DocAccountVerification.propTypes = {
  user: PropTypes.object.isRequired,
  updateVerification: PropTypes.func.isRequired,
};

const mapStateToProps = (state) => ({
  user: state.user,
});

const mapActionsToProps = {
  updateVerification,
};

export default connect(mapStateToProps, mapActionsToProps)(DocAccountVerification);
