              rootData["Specialty"].forEach(specialtyItem => {
                  let specialtyName = specialtyItem['Name'];
                  specialtyName = specialtyName.replace(" ", "").toLowerCase();
                  specialtyItem['Hospital'].forEach((hospital)=> {
                      let locationZip = hospital['Zip'];
                      if (locationZip == locationInfo && userKeyWords == specialtyName){
                          newHosData.push(hospital);
                          newDocData.push.apply(newDocData,hospital["Doctors"] );
                      }
                  });
              });
          } 
      }  else if (searchMethod == 'Doctor'){
          if (locationInfo == ''){
              rootData["Specialty"].forEach(specialtyItem => {
                  let foundDoc = 0;
                  specialtyItem['Hospital'].forEach((hospital)=>{
                      let docName = hospital['Doctors'];
                      docName.forEach((doctor) => {
                          let docName = doctor['DocName'].replace(" ", "").toLowerCase()
                          if (docName == userKeyWords){
                              newDocData.push(doctor);
                              if (foundDoc == 0){
                                newHosData.push(hospital);
                              }
                              foundDoc ++;
                          }

                      });
                  })
              });
          } else {
              rootData["Specialty"].forEach(specialtyItem => {
                  let foundDoc = 0;
                  specialtyItem['Hospital'].forEach((hospital)=>{
                      let locationZip = hospital['Zip'];
                      let docName = hospital['Doctors'];
                      docName.forEach((doctor) => {
                          let docName = doctor['DocName']
                          if (docName.replace(" ", "").toLowerCase() == userKeyWords.toLowerCase() && locationZip == locationInfo){
                              newDocData.push(doctor);
                              foundDoc ++;
                          }
                          if (foundDoc == 1){
                              newHosData.push(hospital);
                          }
                      });
                  })
              });
          } 
      } else if (searchMethod == 'Hospital'){
          if (locationInfo == ''){
              rootData["Specialty"].forEach(specialtyItem => {
                  let foundTarget = true;
                  let hosList = specialtyItem['Hospital'];
                  hosList.forEach((hospital) => {
                      let hosName = hospital['HospitalName'];
                      hosName = hosName.replace(" ", "").toLowerCase();
                      if (userKeyWords.toLowerCase() == hosName){
                          if (foundTarget){
                              newHosData.push(hospital);
                          }
                          newDocData.push.apply(newDocData,hospital["Doctors"]);
                          foundTarget = false;
                      }
                  })
              });
          } else {
              rootData["Specialty"].forEach(specialtyItem => {
                  let foundTarget = true;
                  let hosList = specialtyItem['Hospital'];
                  hosList.forEach((hospital) => {
                      let hosName = hospital['HospitalName'];
                      let locationZip = hospital['Zip'];
                      hosName = hosName.replace(" ", "").toLowerCase();
                      if (userKeyWords.toLowerCase() == hosName && locationZip == locationInfo){
                          if (foundTarget){
                              newHosData.push(hospital);
                          }
                          newDocData.push.apply(newDocData,hospital["Doctors"]);
                          foundTarget = false;
                      }
                  })
              });
          } 

      } else {
          if (locationInfo == ''){
              rootData["Specialty"].forEach(specialtyItem => {
                  let condtionList = specialtyItem['Condition'];
                  condtionList = condtionList.map(function(item) { return item.toLowerCase(); });
                  if (condtionList.includes(userKeyWords)){
                      specialtyItem["Hospital"].forEach((hospital) => {
                          newHosData.push(hospital);
                          hospital['Doctors'].forEach((doctor) => {
                              let docCondition = doctor['Conditions'];
                              docCondition = docCondition.map(function(item) { return item.toLowerCase(); });
                              if (docCondition.includes(userKeyWords)){
                                  newDocData.push(doctor);
                              }
                          });
                      })
                  }
              });
          } else {
              rootData["Specialty"].forEach(specialtyItem => {
                  let condtionList = specialtyItem['Condition'];
                  condtionList = condtionList.map(function(item) { return item.toLowerCase(); });
                  specialtyItem["Hospital"].forEach((hospital) => {
                      let locationZip = hospital['Zip'];
                      if (locationZip == locationInfo && condtionList.includes(userKeyWords)){
                          newHosData.push(hospital);
                          hospital['Doctors'].forEach((doctor) => {
                              let docCondition = doctor['Conditions'];
                              docCondition = docCondition.map(function(item) { return item.toLowerCase(); });
                              if (docCondition.includes(userKeyWords)){
                                  newDocData.push(doctor);
                              }
                          });
                      }

                  });
              });
          } 
      }
      sethospitalInfo(newHosData);
      setDocInfo(newDocData);
      sethospitalInfoCopy(newHosData);
      setDocInfoCopy(newDocData);
      if (pageProps.history != null){
        // reset default search method
        // getSearchMethod('Specialty');
        // // getKeyWords('');
        pageProps.history.push('/results');
      }

    }

    return function resetSearchStatus(){
      setSearchBegin(false);
    }

  }






  const renderHome = (renderProps) => {
    return <Home {...renderProps} 
                    setDocInfo={setDocInfo} 
                    sethospitalInfo={sethospitalInfo} 
                    doMainSearch={doMainSearch}
                    getLocationValue={getLocationValue}
                    getKeyWords={getKeyWords}
                    startSearch={startSearch}
                    getSearchMethod={getSearchMethod}
                    searchBegin={searchBegin}
                    setSearchMethod={setSearchMethod}
                    setKeywords={setKeywords}
                   />
  }
  const renderResults = (renderProps) => {
    return <Results {...renderProps} 
                      setDocInfo={setDocInfo} 
                      sethospitalInfo={sethospitalInfo} 
                      docInfo={docInfo} 
                      hospitalInfo ={hospitalInfo}
                      doMainSearch={doMainSearch}
                      getLocationValue={getLocationValue}
                      getKeyWords={getKeyWords}
                      startSearch={startSearch}
                      searchBegin={searchBegin}
                      setSearchMethod={setSearchMethod}
                      setKeywords={setKeywords}
                      getSearchMethod={getSearchMethod}/>

  }

  return (
    <MuiThemeProvider theme={theme}>
      <Provider store={store}>
        <Router>
          <Switch>
            <div className="container">
              <Route exact path="/" render={renderHome}></Route>
              <AuthRoute exact path="/login" component={login} />
              <AuthRoute exact path="/signup" component={signup} />
              <Route exact path="/covid19" component={covid}></Route>
              <Route exact path="/results" render={renderResults}></Route>
            </div>
          </Switch>
        </Router>
      </Provider>
    </MuiThemeProvider>
  );
}

