import React, { useEffect } from "react";
import { connect } from "react-redux";
import { makeStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import Box from "@material-ui/core/Box";
import Typography from "@material-ui/core/Typography";
import Chip from "@material-ui/core/Chip";
import Grid from "@material-ui/core/Grid";
import IconButton from "@material-ui/core/IconButton";
import Hidden from "@material-ui/core/Hidden";

//icons
import ArrowBackIosIcon from "@material-ui/icons/ArrowBackIos";
import LocationOnOutlinedIcon from "@material-ui/icons/LocationOnOutlined";
import PhoneOutlinedIcon from "@material-ui/icons/PhoneOutlined";
import ErrorOutlineOutlinedIcon from "@material-ui/icons/ErrorOutlineOutlined";
import BookmarkBorderOutlinedIcon from "@material-ui/icons/BookmarkBorderOutlined";
import ShareOutlinedIcon from "@material-ui/icons/ShareOutlined";
import FavoriteIcon from "@material-ui/icons/Favorite";
import FavoriteBorderOutlinedIcon from "@material-ui/icons/FavoriteBorderOutlined";

import PropTypes from "prop-types";

import { changeLikeStatus } from "../../redux/actions/userActions";


//images
import docImg from "../../img/results/docAlex.png";

//components
import CovidAlert from "../Alert";
import DocTags from "./DocTags";

const useStyles = makeStyles((theme) => ({
  covidBox: {
    marginTop: 64,
    [theme.breakpoints.down("sm")]: {
      marginTop: 20,
      marginLeft: 20,
      marginRight: 20,
    },
  },

  //return to doctors button
  returnBox: {
    marginLeft: 50,
    [theme.breakpoints.down("sm")]: {
      marginLeft: 20,
    },
  },

  //doctor image
  img: {
    width: "100%",
    height: "100%",
    objectFit: "contain",
  },

  profileGrid: {
    [theme.breakpoints.down("xs")]: {
      marginLeft: 20,
      marginRight: 20,
    },
  },

  line: {
    [theme.breakpoints.down("xs")]: {
      border: "5px solid rgba(0, 0, 0, 0.05)",
    },
  },
}));

//for Doc image + basic info + likes
//edit tags section is imported from DocTags.js

function DocInfo(props) {
  const classes = useStyles();

  const authenticated = props.authenticated;

  const [renderCount, setRenderCount] = React.useState(0);

  const backToRes = () => {
    if (props.history != null) {
      if (props.backTo == "resultsPage") {
        props.history.push("/results");
      } else if (props.backTo == "profilePage") {
        props.history.push("/hospprofile");
      }
    }
  };

  const [likedInfo, setState] = React.useState({
    hasLiked: false,
    likedList: []
  });

  useEffect(() => {
    if (renderCount == 0) {
      return displayStoredData();
    }
  });

  const displayStoredData = () => {
    // call function to get data from returned props from firebase
    getStoredData()
      .then((res) => {
        // set state
        if (res.likeHistory) {
          if (res.likeHistory.doctors) {
            setState({
              likedList: res.likeHistory.doctors
            })
            if (res.likeHistory.doctors.includes("verify")) {
              setState({
                hasLiked: true
              })
            }
          }
        };
        setRenderCount(1);
      }).catch((error) => {
        console.error(error);
      })
  }

  // wait for returned props from firebase to be ready
  let getStoredData = async () => {
    let storedCredentials = await props.storedCredentials;
    return storedCredentials;
  }

  // if the user has liked the doctor before: filled heart
  // if not: outlined heart

  const LikeIcon = (hasLiked) ? FavoriteIcon : FavoriteBorderOutlinedIcon;

  const toggleLikeUnlike = () => {
    setState(prevState => ({
      hasLiked: !hasLiked
    }))


    if (hasLiked) {
      let likedListCopy = likedList;
      let index = likedListCopy.indexOf("verify");
      likedListCopy.splice(index, 1);
      setState({
        hasLiked: false,
        likedList: likedListCopy
      })
    } else {
      setState(prevState => ({
        hasLiked: true,
        likedList: [
          likedListCopy, "verify"
        ]
      }))
    }
  }

  // wait for the newly uploaded image to be updated to state and post to firebase
  useEffect(() => {
    return toggleLike();
  }, [likedInfo.hasLiked]);

  const toggleLike = () => {
    waitLikeUpdate()
      .then((newLikedList) => {
        // const newLikedList = "";

        // if (liked) {
        //   newLikedList = [likedInfo.likedList, "verify"];
        // } else {
        //   newLikedList = likedInfo.likedList;
        //   let index = newLikedList.indexOf("verify");
        //   newLikedList.splice(index, 1);
        // }

        props.changeLikeStatus(newLikedList);
      }).catch((error) => {
        console.error(error);
      });
  }

  let waitLikeUpdate = async () => {
    let newLikedList = await likedInfo.likedList;
    return newLikedList;
  }


  return (
    <div>
      <div className={classes.covidBox}>
        <CovidAlert />
      </div>

      {/* For 'return to doctors' button (需要换成return to hospital， depending on user之前是怎么搜的) */}
      <Box display="flex" mt={3} mb={3} className={classes.returnBox}>
        <Button
          style={{ fontSize: 16, textTransform: "none" }}
          color="primary"
          onClick={backToRes}
          startIcon={<ArrowBackIosIcon />}
        >
          Return to Doctors
        </Button>
      </Box>

      {/* 手机屏幕才会出现的格式：doctor照片在上面 ，like icon 在右上角*/}
      <Hidden smUp>
        <Grid container display="flex" justifyContent="center">
          <Grid item xs={3}></Grid>
          <Grid item xs={6} align="center">
            {/* doctor image */}
            <div style={{ width: 150, height: 180 }}>
              <img className={classes.img} src={docImg} alt="docimg" />
            </div>
          </Grid>
          {/* Like icon + number of likes */}
          <Grid item xs={3}>
            <Box
              display="flex"
              flexDirection="column"
              justifyContent="center"
              alignItems="center"
            >
              {/* 如果登入了，爱心icon成了iconButton，可以点 , 但是我没写logic, 目前点了的话，这个button不会从
              <FavoriteBorderOutlinedIcon> 换成<FavoriteIcon>, likeCount也不会增加，麻烦你了
              */}
              {authenticated ? (
                <IconButton onClick={toggleLikeUnlike}>
                  <LikeIcon style={{ color: "red" }} />
                </IconButton>
              ) : (
                  <FavoriteIcon style={{ color: "red" }} />
                )}
              {/* like count */}
              <Typography variant="body2" color="primary">
                {props.targetDoc["NumberOfLikes"].toLocaleString(navigator.language, { minimumFractionDigits: 0 })}
              </Typography>
            </Box>
          </Grid>
        </Grid>
      </Hidden>

      <Grid container spacing={0}>
        <Grid item xs={12} sm={5} md={4}>
          <Box
            display="flex"
            flexDirection="column"
            justifyContent="center"
            alignItems="center"
          >
            {/* 大屏幕会出现的格式：doctor照片在左边 */}
            <Hidden xsDown>
              {/* doctor image */}
              <div style={{ width: 200, height: 250 }}>
                <img className={classes.img} src={docImg} alt="docimg" />
              </div>
            </Hidden>

            {/* 手机屏幕出现的格式：doctor's name 在照片下面 */}
            <Hidden smUp>
              <Typography variant="h5" color="primary" style={{ margin: 20 }}>
                {"Dr." + props.targetDoc["DocName"]}
              </Typography>
            </Hidden>

            <Box display="flex" mt={2}>
              {/* report button */}
              <Button
                startIcon={<ErrorOutlineOutlinedIcon />}
                style={{ textTransform: "none" }}
                color="primary"
              >
                Report
              </Button>
              {/* share button */}
              <Button
                startIcon={<ShareOutlinedIcon />}
                style={{ textTransform: "none" }}
                color="primary"
              >
                Share
              </Button>
              {/* save button */}
              <Button
                startIcon={<BookmarkBorderOutlinedIcon />}
                style={{ textTransform: "none" }}
                color="primary"
              >
                Save
              </Button>
            </Box>
          </Box>
        </Grid>

        <Grid item xs={12} sm={6} md={6}>
          {/* 大屏幕会出现的格式：doctor name 在右边 */}
          <Hidden xsDown>
            <Typography variant="h5" color="primary">
              {"Dr." + props.targetDoc["DocName"]}
            </Typography>
          </Hidden>
          <br></br>

          {/* 手机幕会出现的格式：灰色比较粗的divider line */}
          <Hidden smUp>
            <hr className={classes.line}></hr>
          </Hidden>

          {/* Doctor basic info */}
          <Box className={classes.profileGrid} mt={3} mb={3}>
            <Typography variant="body1" color="textPrimary">
              <strong>Specialty: </strong>{" "}
              <span>{props.targetDoc["Specialty"]} </span>
            </Typography>

            <Typography variant="body1" color="textPrimary">
              <strong>Years of Practice: </strong>{" "}
              <span>{props.targetDoc["YearsofPractice"] + " years"} </span>
            </Typography>

            <Typography variant="body1" color="textPrimary">
              <strong>Hospital: </strong>
              <span>{props.targetDoc["Hospital"]}</span>
              {/* private tag */}
              <Chip
                style={{ marginLeft: 10 }}
                color="primary"
                size="small"
                label={props.targetDoc["Type"]}
              ></Chip>
            </Typography>
            <br></br>

            <Box display="flex" flexDirection="row" mt={1}>
              <LocationOnOutlinedIcon style={{ marginRight: 10 }} />
              <Typography variant="body1" color="textPrimary">
                <span>{props.targetDoc["Address"]}</span>
              </Typography>
            </Box>
            <Box display="flex" flexDirection="row" mt={1}>
              <PhoneOutlinedIcon style={{ marginRight: 10 }} />
              <Typography variant="body1" color="textPrimary">
                <span>{props.targetDoc["Phone"]}</span>
              </Typography>
            </Box>
          </Box>
          {/* edit Tag component (imported from Tag.js) */}
          <DocTags tagInfo={props.targetDoc["Tags"]} />
        </Grid>

        <Grid item xs={12} sm={1} md={2}>
          {/* 大屏幕会出现的格式：like icon在最右边 */}
          <Hidden xsDown>
            <Box
              display="flex"
              flexDirection="column"
              justifyContent="center"
              alignItems="center"
            >
              {/* 如果登入了，爱心icon成了iconButton，可以点 , 但是我没写logic, 目前点了的话，这个button不会从
              <FavoriteBorderOutlinedIcon> 换成<FavoriteIcon>, likeCount也不会增加，麻烦你了
              */}
              {authenticated ? (
                <IconButton>
                  <LikeIcon style={{ color: "red" }} />
                </IconButton>
              ) : (
                  <FavoriteIcon style={{ color: "red" }} />
                )}
              <Typography variant="body2" color="primary">
                {props.targetDoc["NumberOfLikes"].toLocaleString(navigator.language, { minimumFractionDigits: 0 })}
              </Typography>
            </Box>
          </Hidden>
        </Grid>
      </Grid>
    </div>
  );
}

const mapStateToProps = (state) => ({
  authenticated: state.user.authenticated,
  storedCredentials: state.user.credentials,
  searchInfo: state.data.searchInfo
});

const mapActionsToProps = {
  changeLikeStatus
};

export default connect(mapStateToProps, mapActionsToProps)(DocInfo);
