import {
  //SET_USER,
  SET_AUTHENTICATED,
  SET_UNAUTHENTICATED,
} from "../types";

// not logged in!
const initialState = {
  authenticated: false,
  loading: false,
  credentials: {},
  //likes: [],
};

//the action that we receive
//depending on action type, we will do something
export default function (state = initialState, action) {
  switch (action.type) {
    /*catch our type */
    /* if we get set_authenticated (call from App.js) spread the state we already have, make authenticated: true*/
    case SET_AUTHENTICATED:
      return {
        ...state,
        authenticated: true,
      };
    /*when we logout, return to initial state*/
    case SET_UNAUTHENTICATED:
      return initialState;

    default:
      return state;
  }
}
