import React from "react";
import PropTypes from "prop-types";
import { makeStyles } from "@material-ui/core/styles";
import Tabs from "@material-ui/core/Tabs";
import Tab from "@material-ui/core/Tab";
import Typography from "@material-ui/core/Typography";
import Box from "@material-ui/core/Box";
import TextField from "@material-ui/core/TextField";
import Autocomplete, {
  createFilterOptions,
} from "@material-ui/lab/Autocomplete";

//filter specialty
const filterSpecialtyOptions = createFilterOptions({
  matchFrom: "start",
  stringify: (option) => option.specialty,
});

//filter doctor
const filterDocOptions = createFilterOptions({
  matchFrom: "start",
  stringify: (option) => option.name,
});

//filter hospital
const filterHospOptions = createFilterOptions({
  matchFrom: "start",
  stringify: (option) => option.hospName,
});

//filter condition
const filterConditionOptions = createFilterOptions({
  matchFrom: "start",
  stringify: (option) => option.condition,
});

// specialties options
const specialties = [
  { specialty: "Allergy and Immunology" },
  { specialty: "Anesthesiology" },
  { specialty: "Arthroplasty" },
];
// doctor name options
const docNames = [
  { name: "Alex Leow" },
  { name: "Alex Tan" },
  { name: "Bryan Lee" },
];
// hospital options
const hospNames = [
  { hospName: "Pantai Hospital Kuala Lumpur" },
  { hospName: "Sunway Medical" },
];
// Conditions options
const conditions = [{ condition: "A" }, { condition: "B" }];

function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <Typography
      component="div"
      role="tabpanel"
      hidden={value !== index}
      id={`full-width-tabpanel-${index}`}
      aria-labelledby={`full-width-tab-${index}`}
      {...other}
    >
      {value === index && <Box p={3}>{children}</Box>}
    </Typography>
  );
}

TabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.any.isRequired,
  value: PropTypes.any.isRequired,
};

function a11yProps(index) {
  return {
    id: `vertical-tab-${index}`,
    "aria-controls": `vertical-tabpanel-${index}`,
  };
}

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    backgroundColor: theme.palette.background.paper,
    display: "flex",
    height: 224,
  },
  tabs: {
    borderRight: `1px solid ${theme.palette.divider}`,
  },
}));

export default function VerticalSearchTabs() {
  const classes = useStyles();
  const [value, setValue] = React.useState(0);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return (
    <div className={classes.root}>
      <Tabs
        orientation="vertical"
        variant="scrollable"
        value={value}
        onChange={handleChange}
        aria-label="Vertical tabs example"
        className={classes.tabs}
      >
        <Tab
          style={{ textTransform: "none" }}
          label="Specialty"
          {...a11yProps(0)}
        />
        <Tab
          style={{ textTransform: "none" }}
          label="Doctor"
          {...a11yProps(1)}
        />
        <Tab
          style={{ textTransform: "none" }}
          label="Hospital"
          {...a11yProps(2)}
        />
        <Tab
          style={{ textTransform: "none" }}
          label="Condition"
          {...a11yProps(3)}
        />
      </Tabs>
      <TabPanel value={value} index={0}>
        <Autocomplete
          options={specialties}
          getOptionLabel={(option) => option.specialty}
          filterOptions={filterSpecialtyOptions}
          renderInput={(params) => (
            <TextField
              {...params}
              label="Search by specialty"
              variant="outlined"
              className={classes.inputRoot}
              style={{ width: 450 }}
            />
          )}
        />
        <br></br>
      </TabPanel>
      <TabPanel value={value} index={1}>
        Item Two
      </TabPanel>
      <TabPanel value={value} index={2}>
        Item Three
      </TabPanel>
      <TabPanel value={value} index={3}>
        Item Four
      </TabPanel>
    </div>
  );
}
