function DocAccountVerification(props) {
  const classes = useStyles();

  // console.log(props.updateVerification)
  // let updateVerification = props.updateVerification;


  const [medicalNumber, setMedicalNumber] = React.useState();
  const [identityCard, setIdentityCard] = React.useState({
    src: "",
    name: ""
  });
  const [medicalDegree, setMedicalDegree] = React.useState({
    src: "",
    name: ""
  });

  // 这个是加的读medical number的function
  const editMedicalNumber = (event) => {
    setMedicalNumber(event.target.value);
  }

  const uploadIdentityCard = (event) => {
    let reader = new FileReader();
    let uploaded = event.target.files[0];
    if (uploaded) {
      reader.readAsDataURL(uploaded);
    }
    reader.onload = () => {
      setIdentityCard({
        src: reader.result,
        name: uploaded.name
      })
    };
  };

  const uploadMedicalDegree = (event) => {
    let reader = new FileReader();
    let uploaded = event.target.files[0];
    if (uploaded) {
      reader.readAsDataURL(uploaded);
    }
    reader.onload = () => {
      setMedicalDegree({
        src: reader.result,
        name: uploaded.name
      })
    };
  };

  // initiate push to firebase when submit button is clicked

  // 这边按submit就会trigger user actions里面的updateVerification
  // 我
  const submitVerification = (event) => {
    event.preventDefault();
    const verificationInfo = {
      medicalNumber: medicalNumber,
      identityCardSrc: identityCard["src"],
      medicalDegreeSrc: medicalDegree["src"],
    };
    props.updateVerification({ verificationInfo });
  };


  return (
    <div>
      <Navbar />
      <Hidden smDown>
        <div className={classes.covidBox} style={{ position: "relative" }}>
          <CovidAlert />

          <Grid container spacing={0}>
            <Grid item md={4} lg={3}>
              <DocSideNav />
            </Grid>

            <Grid item sm={12} md={8} lg={9}>

              {/* <Verification updateVerification={props.updateVerification} /> */}
              <a id="accountverification" className={classes.anchor}>
                <Grid container spacing={0}>
                  <Grid item xs={1}></Grid>
                  <Grid item xs={10} md={8}>
                    <Box flexGrow={1} flexDirection="row" mb={1}>
                      <Typography variant="h5" color="primary">
                        {/* Back button 手机屏幕才会出玄 */}
                        <Hidden mdUp>
                          {/* <IconButton> */}
                          <Link to='docaccount'>
                            <ArrowBackIosIcon />
                          </Link>
                          {/* </IconButton> */}
                        </Hidden>
                        <strong>Account Verification</strong>
                      </Typography>
                    </Box>

                    <br></br>
                    <Box display="flex" flexDirection="row">
                      {/* 现在是ACCOUNT NOT VERIFIED, 需要换icon 如果account pending/verified */}
                      <CancelIcon style={{ color: "red", marginRight: 10 }} />
                      <Typography variant="body1">
                        Account not verified<br></br>
                      </Typography>
                    </Box>
                    <br></br>
                    <br></br>
                    {/* Meidcal reg number */}
                    <TextField
                      fullWidth
                      required
                      label="Medical Registration Number"
                      variant="outlined"
                      onChange={editMedicalNumber}
                    />
                    {/* Upload malaysian identity card */}
                    <Box display="flex" mt={5} mb={3}>
                      <Box flexGrow={1}>
                        <Typography variant="body1" color="textSecondary">
                          Malaysian Identity Card
              </Typography>
                      </Box>
                      <Box flexGrow={1}>
                        <input
                          accept="image/*"
                          style={{ display: "none" }}
                          id="contained-button-identity"
                          multiple
                          type="file"
                          onChange={uploadIdentityCard}
                        />
                        <label htmlFor="contained-button-identity">
                          <Button
                            startIcon={<CloudUploadOutlinedIcon />}
                            variant="outlined"
                            component="span"
                            color="primary"
                            style={{ textTransform: "none", float: "right" }}
                            size="medium"
                          >
                            Upload
              </Button>
                        </label>
                      </Box>
                    </Box>
                    <Typography variant="body2">{identityCard.name}</Typography>
                    {/* Upload medical degree */}
                    <Box display="flex" mt={5} mb={5}>
                      <Box flexGrow={1}>
                        <Typography variant="body1" color="textSecondary">
                          Certified copy of Medical Degree
              </Typography>
                      </Box>
                      <Box>
                        <input
                          accept="image/*"
                          style={{ display: "none" }}
                          id="contained-button-medical"
                          multiple
                          type="file"
                          onChange={uploadMedicalDegree}
                        />
                        <label htmlFor="contained-button-medical">
                          <Button
                            startIcon={<CloudUploadOutlinedIcon />}
                            variant="outlined"
                            component="span"
                            color="primary"
                            style={{ textTransform: "none", float: "right" }}
                            size="medium"
                          >
                            Upload
              </Button>
                        </label>
                      </Box>
                    </Box>
                    <Typography variant="body2">{medicalDegree.name}</Typography>

                    <Button
                      variant="contained"
                      color="primary"
                      style={{ textTransform: "none", float: "right" }}
                      size="large"
                      onClick={submitVerification}
                    >
                      Submit
          </Button>
                    <br></br>
                    <br></br>
                  </Grid>
                  <Grid item xs={1} md={3}></Grid>
                </Grid>
              </a>
              );


              {/* <DocCard /> */}
            </Grid>
          </Grid>
        </div>
      </Hidden>
      <Hidden mdUp>
        {/* <Verification /> */}
        <DocSideNav />
      </Hidden>
    </div>
  );
}


