import React, { Fragment } from "react";

// material ui
import { withStyles } from "@material-ui/core/styles";
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";

import Tabs from "@material-ui/core/Tabs";
import Tab from "@material-ui/core/Tab";
import Typography from "@material-ui/core/Typography";
import Box from "@material-ui/core/Box";
import TextField from "@material-ui/core/TextField";
import Location from "./Location";
import SearchIcon from "@material-ui/icons/Search";
import Button from "@material-ui/core/Button";

import Autocomplete, {
  createFilterOptions,
} from "@material-ui/lab/Autocomplete";

const filterSpecialtyOptions = createFilterOptions({
  matchFrom: "start",
  stringify: (option) => option.specialty,
});
const filterDocOptions = createFilterOptions({
  matchFrom: "start",
  stringify: (option) => option.name,
});

const filterHospOptions = createFilterOptions({
  matchFrom: "start",
  stringify: (option) => option.hospName,
});

const filterConditionOptions = createFilterOptions({
  matchFrom: "start",
  stringify: (option) => option.condition,
});

function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return value === index && <Box mt={2}>{children}</Box>;
}

function a11yProps(index) {
  return {
    id: `simple-tab-${index}`,
    "aria-controls": `simple-tabpanel-${index}`,
  };
}

const StyledTabs = withStyles({
  indicator: {
    display: "flex",
    justifyContent: "center",
    backgroundColor: "transparent",
    "& > div": {
      width: "100%",
      backgroundColor: "#FF8686",
    },
  },
})((props) => <Tabs {...props} TabIndicatorProps={{ children: <div /> }} />);

const StyledTab = withStyles((theme) => ({
  root: {
    textTransform: "none",
    color: "#fff",
    fontWeight: "bold",
    fontSize: "1em",
    marginTop: 10,
    "&:focus": {
      opacity: 1,
    },
    [theme.breakpoints.down("sm")]: {
      fontSize: "0.75em",
    },
    [theme.breakpoints.down("xs")]: {
      fontSize: "0.7em",
    },
  },
}))((props) => <Tab disableRipple {...props} />);

export default function SearchTabs() {
  const classes = useStyles();

  const [value, setValue] = React.useState(0);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return (
    <div>
      <div className={classes.tabContainer}>
        <StyledTabs
          value={value}
          onChange={handleChange}
          aria-label="search tabs"
          className={classes.styledTabs}
        >
          <StyledTab
            label="Specialty"
            {...a11yProps(0)}
            style={{ minWidth: 10 }}
          />
          <StyledTab
            label="Doctor"
            {...a11yProps(1)}
            style={{ minWidth: 10 }}
          />
          <StyledTab
            label="Hospital"
            {...a11yProps(2)}
            style={{ minWidth: 10 }}
          />
          <StyledTab
            label="Condition"
            {...a11yProps(3)}
            style={{ minWidth: 10 }}
          />
        </StyledTabs>
      </div>

      <Typography />
      <TabPanel value={value} index={0}>
        <Autocomplete
          options={specialties}
          getOptionLabel={(option) => option.specialty}
          filterOptions={filterSpecialtyOptions}
          //style={{ width: 480 }}
          //fullWidth
          renderInput={(params) => (
            <TextField
              {...params}
              label="Search by specialty"
              variant="filled"
              className={classes.inputRoot}
              InputProps={{ ...params.InputProps, disableUnderline: true }}
            />
          )}
        />
        <br></br>
      </TabPanel>

      <TabPanel value={value} index={1}>
        <Autocomplete
          options={docNames}
          getOptionLabel={(option) => option.name}
          filterOptions={filterDocOptions}
          //style={{ width: 480 }}
          renderInput={(params) => (
            <TextField
              {...params}
              label="Search by doctor's name"
              variant="filled"
              className={classes.inputRoot}
              InputProps={{ ...params.InputProps, disableUnderline: true }}
            />
          )}
        />
        <br></br>
      </TabPanel>

      <TabPanel value={value} index={2}>
        <Autocomplete
          options={hospNames}
          getOptionLabel={(option) => option.hospName}
          filterOptions={filterHospOptions}
          renderInput={(params) => (
            <TextField
              {...params}
              label="Search by hospital's name"
              variant="filled"
              className={classes.inputRoot}
              InputProps={{ ...params.InputProps, disableUnderline: true }}
            />
          )}
        />
        <br></br>
      </TabPanel>
      <TabPanel value={value} index={3}>
        <Autocomplete
          options={conditions}
          getOptionLabel={(option) => option.condition}
          filterOptions={filterConditionOptions}
          renderInput={(params) => (
            <TextField
              {...params}
              label="Search by medical condition"
              variant="filled"
              className={classes.inputRoot}
              InputProps={{ ...params.InputProps, disableUnderline: true }}
            />
          )}
        />
        <br></br>
      </TabPanel>
    </div>
  );
}

const specialties = [
  { specialty: "Allergy and Immunology" },
  { specialty: "Anesthesiology" },
  { specialty: "Arthroplasty" },
];

const docNames = [
  { name: "Alex Lee" },
  { name: "Alex Tan" },
  { name: "Bryan Leow" },
];

const hospNames = [{ condition: "A" }, { condition: "B" }];

const conditions = [
  { hospName: "Pantai Hospital Kuala Lumpur" },
  { hospName: "Sunway Medical" },
];
const useStyles = makeStyles((theme) => ({
  inputRoot: {
    "& .MuiFilledInput-root": {
      backgroundColor: "rgba(255,255,255)",
      borderRadius: 4,
    },
    [theme.breakpoints.down("md")]: {
      variant: "outline",
    },
  },

  tabContainer: {
    [theme.breakpoints.down("xs")]: {
      display: "flex",
      flexDirection: "column",
      justifyContent: "center",
      alignItems: "center",
    },
  },
}));
