import React, { Fragment, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Box from "@material-ui/core/Box";
import Typography from "@material-ui/core/Typography";
import Grid from "@material-ui/core/Grid";
import Hidden from "@material-ui/core/Hidden";
import ArrowBackIosIcon from "@material-ui/icons/ArrowBackIos";
import ToggleButton from "@material-ui/lab/ToggleButton";
import ToggleButtonGroup from "@material-ui/lab/ToggleButtonGroup";
import CircularProgress from '@material-ui/core/CircularProgress';
import { Link } from "react-router-dom";

import { connect } from "react-redux";
import PropTypes from "prop-types";
import axios from "axios";

import DocCard from "../../Components/results/DocCard";
import HospCard from "../../Components/results/HospitalCard";

// import { getAllSearchData } from "../../redux/actions/dataActions";


const proxyurl = "https://cors-anywhere.herokuapp.com/";

const useStyles = makeStyles((theme) => ({
  ...theme.account,
}));

function LikeHistory(props) {
  const classes = useStyles();

  const [display, setDisplay] = React.useState("doctor"); //display by doctor as default
  const handleDisplay = (event, newDisplay) => {
    if (newDisplay != null) {
      setDisplay(newDisplay);
    }
  };

  const [renderCount, setRenderCount] = React.useState(0);

  const [likeHistory, setLikeHistory] = React.useState({
    doctors:[],
    hospitals:[]
  })


  useEffect(() => {
    if (renderCount == 0) {
      return displayStoredData();
    }
  });

  const displayStoredData = () => {
    getStoredData()
      .then((res) => {
        let likedDoctors;
        let likedHospitals;

        // if the user has not never liked any doctors or hospitals
        if (!res.likeHistory) {
          likedDoctors = [];
          likedHospitals = [];
        } else {
          likedDoctors = res.likeHistory.doctors ? res.likeHistory.doctors : [];
          likedHospitals = res.likeHistory.hospitals ? res.likeHistory.hospitals : [];
        }

        setLikeHistory({
          doctors: likedDoctors,
          hospitals: likedHospitals
        });

        setRenderCount(1);
      }).catch((error) => {
        console.error(error);
      })
  }

  // wait for returned props from firebase to be ready
  let getStoredData = async () => {
    let userStoredCredentials = await props.storedCredentials;
    console.log(userStoredCredentials)
    return userStoredCredentials;
  }







  // axios.get(proxyurl+axios.defaults.baseURL+"getallsearchdata", {
  //   params:{
  //     specialtyList:["Gastroenterology"]
  //   }
  // }).then((res)=>{
  //   console.log(res);

  // })

  axios.get(proxyurl+axios.defaults.baseURL+"user")
    .then((res)=> {
    console.log(res);

  })






  console.log(likeHistory)

  if (renderCount == 0) {
    // loading spinner if prop data is not yet available
    return (
      <div>
        <CircularProgress color="secondary" style={{ marginLeft: '45%', marginTop: '10%' }} />
      </div>
    );

  } else {



  return (
    <a id="likehistory" className={classes.anchor}>
      <Grid container spacing={0}>
        <Grid item xs={1}></Grid>
        <Grid item xs={10} md={8}>
          <Box display="flex" mt={4} mb={3} flexWrap="wrap" alignItems="center">
            <Box flexGrow={1} flexDirection="row" mb={1}>
              <Typography variant="h5" color="primary">
                {/* Back button, 手机屏幕才会出现 */}
                <Hidden mdUp>
                  {/* <IconButton> */}
                  <Link to="docaccount">
                    <ArrowBackIosIcon
                      className={classes.backIcon}
                      fontSize="small"
                    />
                  </Link>
                  {/* </IconButton> */}
                </Hidden>
                <strong>Like History</strong>
              </Typography>
            </Box>
            {/* Display by Doctor/Hospital buttons */}
            <Box>
              <ToggleButtonGroup
                value={display}
                exclusive
                onChange={handleDisplay}
              >
                <ToggleButton value="doctor" color="primary">
                  <Typography color="primary" style={{ textTransform: "none" }}>
                    Doctor
                  </Typography>
                </ToggleButton>
                <ToggleButton value="hospital">
                  <Typography color="primary" style={{ textTransform: "none" }}>
                    Hospital
                  </Typography>
                </ToggleButton>
              </ToggleButtonGroup>
            </Box>{" "}
          </Box>

          <br></br>
          <br></br>

          {/* if user clicks on display by 'doctor', then render doctor cards */}
          {display === "doctor" ? (
            <Fragment>doccard</Fragment>
          ) : (
            <Fragment>hospcard</Fragment>
          )}
        </Grid>
        <Grid item xs={1} md={3}></Grid>
      </Grid>
    </a>
  );
  }
}







LikeHistory.propTypes = {
  // updateVerification: PropTypes.func.isRequired,
  // getAllSearchData: PropTypes.func.isRequired
};

const mapStateToProps = (state) => ({
  storedCredentials: state.user.credentials,
  // searchData: state.data.searchData
});

const mapActionsToProps = {
  // updateVerification,
  // getAllSearchData
};

export default connect(mapStateToProps, mapActionsToProps)(LikeHistory);
