import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Drawer from "@material-ui/core/Drawer";
import CssBaseline from "@material-ui/core/CssBaseline";
import Toolbar from "@material-ui/core/Toolbar";
import List from "@material-ui/core/List";
import Typography from "@material-ui/core/Typography";
import Divider from "@material-ui/core/Divider";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemText from "@material-ui/core/ListItemText";
import InboxIcon from "@material-ui/icons/MoveToInbox";
import MailIcon from "@material-ui/icons/Mail";
import CovidAlert from "../Alert";
import Box from "@material-ui/core/Box";
import Radio from "@material-ui/core/Radio";
import RadioGroup from "@material-ui/core/RadioGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import FormControl from "@material-ui/core/FormControl";
import FormLabel from "@material-ui/core/FormLabel";
import FormGroup from "@material-ui/core/FormGroup";

import Checkbox from "@material-ui/core/Checkbox";

const drawerWidth = 240;

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
    height: "100vh",
  },
  appBar: {
    zIndex: theme.zIndex.drawer + 1,
  },
  drawer: {
    width: drawerWidth,
    flexShrink: 0,
  },
  drawerPaper: {
    width: drawerWidth,
  },
  drawerContainer: {
    overflow: "auto",
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(3),
  },
}));

function HospitalType() {
  const [value, setValue] = React.useState("Both");

  const handleChange = (event) => {
    setValue(event.target.value);
  };

  return (
    <FormControl component="fieldset">
      <FormLabel component="legend">Hospital Type</FormLabel>
      <br></br>
      <RadioGroup
        aria-label="hospital-type"
        name="hospital-type"
        value={value}
        onChange={handleChange}
      >
        <FormControlLabel value="both" control={<Radio />} label="Both" />
        <FormControlLabel value="public" control={<Radio />} label="Public" />
        <FormControlLabel value="private" control={<Radio />} label="Private" />
      </RadioGroup>
    </FormControl>
  );
}

function Languages() {
  const [state, setState] = React.useState({
    english: false,
    malay: false,
    mandarin: false,
    tamil: false,
    cantonese: false,
  });

  const handleChange = (event) => {
    setState({ ...state, [event.target.name]: event.target.checked });
  };

  const { english, malay, mandarin, tamil, cantonese } = state;

  return (
    <FormControl component="fieldset">
      <FormLabel component="legend">Languages</FormLabel>
      <br></br>
      <FormGroup>
        <FormControlLabel
          control={
            <Checkbox
              checked={english}
              onChange={handleChange}
              name="english"
            />
          }
          label="English"
        />
        <FormControlLabel
          control={
            <Checkbox checked={malay} onChange={handleChange} name="malay" />
          }
          label="Malay"
        />
        <FormControlLabel
          control={
            <Checkbox
              checked={mandarin}
              onChange={handleChange}
              name="mandarin"
            />
          }
          label="Mandarin"
        />
        <FormControlLabel
          control={
            <Checkbox checked={tamil} onChange={handleChange} name="tamil" />
          }
          label="Tamil"
        />
        <FormControlLabel
          control={
            <Checkbox
              checked={cantonese}
              onChange={handleChange}
              name="cantonese"
            />
          }
          label="Cantonese"
        />
      </FormGroup>
    </FormControl>
  );
}

export default function ClippedDrawer() {
  const classes = useStyles();

  return (
    <div className={classes.root}>
      <CssBaseline />

      <Drawer
        className={classes.drawer}
        variant="permanent"
        classes={{
          paper: classes.drawerPaper,
        }}
      >
        <Toolbar />
        <div className={classes.drawerContainer}>
          <Box
            mt={5}
            display="flex"
            flexDirection="column"
            justifyContent="center"
            ml={8}
          >
            <HospitalType />
            <br></br>
            <br></br>

            <Languages />
          </Box>

          <List>
            {["All mail", "Trash", "Spam"].map((text, index) => (
              <ListItem button key={text}>
                <ListItemIcon>
                  {index % 2 === 0 ? <InboxIcon /> : <MailIcon />}
                </ListItemIcon>
                <ListItemText primary={text} />
              </ListItem>
            ))}
          </List>
        </div>
      </Drawer>
      <main className={classes.content}>
        <Toolbar />
        <div className={classes.covidBox}>
          <CovidAlert />
        </div>
        <Typography>hi</Typography>
      </main>
    </div>
  );
}
