import React, { Fragment } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import Box from "@material-ui/core/Box";
import Paper from "@material-ui/core/Paper";
import headerImg from "../../img/home/doctors-heart.png";
import { Typography } from "@material-ui/core";
import SearchTabs from "./SearchTabs";
import Location from "./Location";
import SearchIcon from "@material-ui/icons/Search";
import Button from "@material-ui/core/Button";
import IconButton from "@material-ui/core/IconButton";
import Hidden from "@material-ui/core/Hidden";
import CovidAlert from "../Alert";
const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },

  container: {
    marginTop: 64,
    [theme.breakpoints.down("sm")]: {
      marginTop: 2,
    },
    [theme.breakpoints.down("xs")]: {
      marginTop: 1,
    },
  },
  grid: {
    position: "relative",
  },
  headerImg: {
    width: "100%",
    //position: "absolute",

    //right: 0,

    //display: "block",
    //bottom: 0,

    [theme.breakpoints.up("md")]: {
      width: "75%",
    },
    [theme.breakpoints.up("lg")]: {
      width: "100%",
    },
  },
  /*box: {
    position: "relative",
  }, */

  headerBg: {
    width: "100%",
  },

  root: {
    display: "flex",
    flexDirection: "row",
    alignItems: "center",
    //align-items: center;
    //justify-content: center;
    //padding-top: 15%;
    //text-align: start;
    //padding-left: 2%;
    //backgroundImage: `url(${bg})`,
    backgroundColor: "#003367",
    /*backgroundRepeat: "no-repeat",
    backgroundPosition: "center center",
    backgroundSize: "100% 100%",
    width: "100vw",
    height: "100vh", */
    /*[theme.breakpoints.down("md")]: {
      backgroundColor: "#003367",
      backgroundImage: "none",
      height: "100%", */

    //height: "60vh",
    //marginTop: 0,
  },

  title: {
    color: "white", //"#FF8686",
    fontWeight: "bold",
    [theme.breakpoints.down("xs")]: {
      fontSize: 24,
      //textAlign: "center",
    },
    [theme.breakpoints.down("sm")]: {
      fontSize: 26,
    },
  },
  body: {
    color: "white",
    textAlign: "center",
  },

  headings: {
    //marginTop: 80,
    //marginLeft: 100,
    [theme.breakpoints.down("xs")]: {
      textAlign: "center",
    },
  },

  location: {
    //float: "left",
    //position: "relative",
  },
  /*search: {
    //float: "right",
    //position: "absolute",
    height: 54,
    width: 32,
    [theme.breakpoints.down("md")]: {
      display: "none",
    },
  }, */
  search: {
    marginTop: 20,
    height: 48,
    textTransform: "none",
    fontSize: 16,
  },

  /* searchXs: {
    [theme.breakpoints.up("md")]: {
      display: "none",
    },
  }, */

  locationBox: {
    [theme.breakpoints.up("md")]: {
      display: "flex",
      flexDirection: "row",
    },
  },

  rightGrid: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
  },
  /* leftGrid: {
    marginTop: 200,
    marginLeft: 30,
    //margin: "auto",
  },

  rightGrid: {
    marginTop: 80,
  }, */
}));
/*
const headerBackground = {
  backgroundImage: "url(" + headerBg + ")",
}; */
export default function Header() {
  const classes = useStyles();
  //className={classes.paper}
  /*<img
  src={headerBg}
  alt="doctor-hearts"
  className={classes.headerBg}
/> */

  return (
    <div className={classes.container}>
      <CovidAlert />

      {/* <Paper square elevation={0} className={classes.paper}> */}
      <div className={classes.root}>
        <Grid
          container
          spacing={0}
          className={classes.grid}
          //direction="column"
          //alignItems="center"
          //justify="center"
        >
          <Grid item xs={1} sm={1} md={1} lg={2}></Grid>
          <Grid item xs={10} sm={5} md={5} lg={4} className={classes.leftGrid}>
            <Box mt={16} mb={12} className={classes.box}>
              <div className={classes.headings}>
                <Typography variant="h4" className={classes.title}>
                  Find your Ideal Doctor
                </Typography>
                <br></br>
                <Typography variant="title" className={classes.body}>
                  Quick access to doctor information in Malaysia
                </Typography>
                <br></br>
                <br></br>
                <SearchTabs />
                <Location className={classes.location}></Location>
                <Button
                  variant="contained"
                  color="secondary"
                  size="md"
                  className={classes.search}
                  startIcon={<SearchIcon />}
                  fullWidth
                >
                  Search
                </Button>

                {/* <Box className={classes.locationBox}>
                <Box>
                  <Location className={classes.location}></Location>
                </Box>
                <Box ml={2}>
                  <Button
                    variant="contained"
                    color="secondary"
                    size="md"
                    className={classes.searchXs}
                    fullWidth
                  >
                    Search
                  </Button>
                </Box>
              </Box> */}
                {/* </Paper> */}
              </div>
            </Box>
          </Grid>
          <Grid item sm={1} md={1} />

          <Grid item xs={1} sm={4} md={5} lg={3} className={classes.rightGrid}>
            <Hidden xsDown>
              <img
                src={headerImg}
                alt="doctor-hearts"
                className={classes.headerImg}
              />
            </Hidden>
          </Grid>
          <Grid item lg={2} />
        </Grid>
      </div>
      {/* </Paper> */}
    </div>
  );
}

