import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";
import Box from "@material-ui/core/Box";
import Divider from "@material-ui/core/Divider";
import Hidden from "@material-ui/core/Hidden";
import IconButton from "@material-ui/core/IconButton";
import NavigateBeforeIcon from "@material-ui/icons/NavigateBefore";
import NavigateNextIcon from "@material-ui/icons/NavigateNext";
import Carousel from "react-elastic-carousel";

//components
import TopRatedDocCard from "./TopRatedDocCard";

const useStyles = makeStyles((theme) => ({
  divider: {
    height: 2,
    backgroundColor: "#FF8686",
  },

  line: {
    border: "0.5px solid rgba(0, 0, 0, 0.2)",
    [theme.breakpoints.down("xs")]: {
      border: "5px solid rgba(0, 0, 0, 0.05)",
    },
  },

  navigateIcon: {
    color: "003367",
    fontSize: 60,
    [theme.breakpoints.down("xs")]: {
      fontSize: 25,
    },
  },

  topRatedDocBox: {
    [theme.breakpoints.up("sm")]: {
      justifyContent: "center",
      alignItems: "center",
    },
    [theme.breakpoints.down("sm")]: {
      justifyContent: "left",
      alignItems: "left",
      marginLeft: 20,
    },
  },

  thinLine: {
    border: "0.5px solid rgba(0, 0, 0, 0.2)",
    [theme.breakpoints.down("xs")]: {
      border: "5px solid rgba(0, 0, 0, 0.05)",
    },
  },
}));

// for Top Rated Doctors, Top Rated Specialty, Insurance Accepted section (used in HospProfile.js under pages folder)
export default function DocInfo(props) {
  const classes = useStyles();

  // carousel 显示的卡片数量 （eshin added 5/4/20)
  const breakPoints = [{ itemsToShow: 3 }];

  // create procedure List
  let specialities = props.targetHos["TopSpecialty"].map((spec) => {
    let specCards = (
      <p>{spec["SpecialtyName"] + " (" + spec["Likes"] + ") "}</p>
    );
    return specCards;
  });

  // create condition List
  let insuranceList = props.targetHos["Insurance"].map((insurance) => {
    let insuranceCards = <p>{insurance}</p>;
    return insuranceCards;
  });

  return (
    <Grid container spacing={0}>
      <Grid item xs={12}>
        <Box
          display="flex"
          flexDirection="column"
          mb={2}
          mt={2}
          className={classes.topRatedDocBox}
        >
          <Typography variant="h6" color="primary">
            Top Rated Doctors
          </Typography>
          <Divider className={classes.divider} style={{ width: 170 }} />
          <br></br>
          <br></br>
          {/* 这里出现top rated doctors 的卡片*/}
          {/* eshin added 5/4/20 */}
          <Carousel breakPoints={breakPoints}>
            <TopRatedDocCard />
            <TopRatedDocCard />
            <TopRatedDocCard />
            <TopRatedDocCard />
            <TopRatedDocCard />
            <TopRatedDocCard />
          </Carousel>
        </Box>
        <br></br>
        <hr className={classes.thinLine}></hr>
      </Grid>
      {/* 以下是top rated specialities + accepted insurance section */}
      <Grid container spacing={0}>
        <Grid item sm={1}></Grid>
        <Grid item xs={12} sm={4}>
          <Box
            display="flex"
            flexDirection="column"
            justifyContent="center"
            mb={2}
            ml={2}
            mt={2}
          >
            {/* top rated specialties */}
            <Typography variant="h6" color="primary">
              Top Rated Specialities
            </Typography>
            <Divider className={classes.divider} style={{ width: 200 }} />
            <Typography variant="body1">{specialities}</Typography>
          </Box>
          <Hidden smUp>
            <hr className={classes.line}></hr>
          </Hidden>
        </Grid>
        <Grid item sm={2}></Grid>
        {/* insurance accepted */}
        <Grid item xs={12} sm={4}>
          <Box
            display="flex"
            flexDirection="column"
            justifyContent="center"
            mb={2}
            ml={2}
            mt={2}
          >
            <Typography variant="h6" color="primary">
              Insurance Accepted
            </Typography>
            <Divider className={classes.divider} style={{ width: 180 }} />
            <Typography variant="body1">{insuranceList}</Typography>
          </Box>
        </Grid>
      </Grid>
    </Grid>
  );
}
