import React, { Fragment } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import headerBg from "../../img/home/header_bg.png";
import Box from "@material-ui/core/Box";
import Paper from "@material-ui/core/Paper";
import headerImg from "../../img/home/doctors-heart.png";
import { Typography } from "@material-ui/core";
import SearchTabs from "./SearchTabs";
import Location from "./Location";
import SearchIcon from "@material-ui/icons/Search";
import Button from "@material-ui/core/Button";
import IconButton from "@material-ui/core/IconButton";

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },

  grid: {
    position: "relative",
  },
  headerImg: {
    width: "30%",
    position: "absolute",
    right: 0,
    marginTop: 200,
    marginRight: 100,
  },

  headerBg: {
    width: "100%",
  },

  root: {
    display: "flex",
    flexDirection: "row",
    //align-items: center;
    //justify-content: center;
    //padding-top: 15%;
    //text-align: start;
    //padding-left: 2%;
    backgroundImage: `url(${headerBg})`,
    backgroundRepeat: "no-repeat",
    backgroundPosition: "center center",
    backgroundSize: "100% 100%",
    width: "100%",
    [theme.breakpoints.down("lg")]: {
      height: "48vh",
    },
    [theme.breakpoints.down("md")]: {
      height: "78vh",
    },
    [theme.breakpoints.up("lg")]: {
      height: "100vh",
    },
    [theme.breakpoints.down("md")]: {
      backgroundImage: "none",
    },
  },

  title: {
    color: "#fff",
  },

  headings: {
    marginTop: 120,
    //marginLeft: 100,
  },

  location: {
    //float: "left",
    //position: "relative",
  },
  /*search: {
    //float: "right",
    //position: "absolute",
    height: 54,
    width: 32,
    [theme.breakpoints.down("md")]: {
      display: "none",
    },
  }, */
  search: {
    marginTop: 20,
    height: 48,
  },

  /* searchXs: {
    [theme.breakpoints.up("md")]: {
      display: "none",
    },
  }, */

  locationBox: {
    [theme.breakpoints.up("md")]: {
      display: "flex",
      flexDirection: "row",
    },
  },

  /* leftGrid: {
    marginTop: 200,
    marginLeft: 30,
    //margin: "auto",
  },

  rightGrid: {
    marginTop: 80,
  }, */
}));
/*
const headerBackground = {
  backgroundImage: "url(" + headerBg + ")",
}; */
export default function Header() {
  const classes = useStyles();
  //className={classes.paper}
  /*<img
  src={headerBg}
  alt="doctor-hearts"
  className={classes.headerBg}
/> */

  return (
    <div className={classes.container}>
      {/* <Paper square elevation={0} className={classes.paper}> */}
      <div className={classes.root}>
        <Grid
          container
          spacing={0}
          className={classes.grid}
          //direction="column"
          //alignItems="center"
          //justify="center"
        >
          <Grid item sm={1}></Grid>
          <Grid item xs={12} sm={5} className={classes.leftGrid}>
            <div className={classes.headings}>
              <Typography variant="h3" className={classes.title}>
                Find your Ideal Doctor
              </Typography>
              <br></br>
              <Typography variant="h6" className={classes.title}>
                Quick access to doctor information in Malaysia
              </Typography>
              <br></br>
              {/* <Paper 
                elevation={0}
                style={{
                  display: "inline-block",
                  background: "rgba(0,0,0,0)",
                }}
              > */}
              <SearchTabs />
              <Location className={classes.location}></Location>
              <Button
                variant="contained"
                color="secondary"
                size="md"
                className={classes.search}
                startIcon={<SearchIcon />}
              >
                Search
              </Button>

              {/* <Box className={classes.locationBox}>
                <Box>
                  <Location className={classes.location}></Location>
                </Box>
                <Box ml={2}>
                  <Button
                    variant="contained"
                    color="secondary"
                    size="md"
                    className={classes.searchXs}
                    fullWidth
                  >
                    Search
                  </Button>
                </Box>
              </Box> */}
              {/* </Paper> */}
            </div>
          </Grid>
          <Grid item sm={2}></Grid>

          <Grid item sm={4} className={classes.rightGrid}>
            <img
              src={headerImg}
              alt="doctor-hearts"
              className={classes.headerImg}
            />
          </Grid>
        </Grid>
      </div>
      {/* </Paper> */}
    </div>
  );
}

