// 以后会用到
import {
    // LOADING_USER,
    GET_DATA
} from "../types";
import axios from "axios";


// used in App.js to fetch user data
export const getSearchInfo = () => (dispatch) => {
    axios
        .get("/searchinfo")
        .then((res) => {
            dispatch({
                type: GET_DATA /* use in userReducer.js */,
                payload: res.data,
            });
        })
        .catch((err) => console.log(err));
};

// used in DocTags.js
export const updateDoctorTags = (doctorTags) => {
    axios
        .post("/updatedoctortags", doctorTags)
        .catch((err) => console.log(err));
};

// used in 
export const updateDoctorLikes = (likes) => {
    axios
        .post("/updatedoctortags", likes)
        .catch((err) => console.log(err));
};
