export const getAllSearchData = () => (dispatch) => {
    axios
        .get("/getallsearchdata")
        .then((res) => {
            console.log(res)
            dispatch({
                type: GET_SEARCH_DATA,
                payload: res.data,
            });
        })
        .catch((err) => console.log(err));
};


export const getAllSearchDataHospital = () => (dispatch) => {
    axios
        .get("/hospsearchinfo")
        .then((res) => {
            dispatch({
                type: GET_HOSP_DATA /* use in userReducer.js */,
                payload: res.data,
            });
        })
        .catch((err) => console.log(err));
};

// used in App.js to fetch user data
export const getDoctorUserSearchInfo = () => (dispatch) => {
    dispatch({ type: LOADING_USER });
    axios
      .get("/user")
      .then((res) => {
        dispatch({
          type: SET_USER /* use in userReducer.js */,
          payload: res.data,
        });
      })
      .catch((err) => console.log(err));
  };
