import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import clsx from "clsx";
import Card from "@material-ui/core/Card";
import CardHeader from "@material-ui/core/CardHeader";
import CardContent from "@material-ui/core/CardContent";
import Collapse from "@material-ui/core/Collapse";
import Avatar from "@material-ui/core/Avatar";
import IconButton from "@material-ui/core/IconButton";
import Typography from "@material-ui/core/Typography";
import hospitalIcon from "../../img/covid/hospitalIcon.png";
import hosptreatment from "../../img/covid/hosptreatment.jpg";
import { CardMedia } from "@material-ui/core";

import ExpandMoreIcon from "@material-ui/icons/ExpandMore";
const useStyles = makeStyles((theme) => ({
  root: {
    width: "100%",
    marginBottom: 40,
  },
  cardHeader: {
    color: "#003367",
    fontFamily: "'Raleway', sans-serif",
  },

  body: {
    fontFamily: '"Montserrat", sans-serif',
  },

  subtitle: {
    fontFamily: '"Montserrat", sans-serif',
    color: "#003367",
  },

  expand: {
    transform: "rotate(0deg)",
    marginLeft: "auto",
    transition: theme.transitions.create("transform", {
      duration: theme.transitions.duration.shortest,
    }),
  },
  expandOpen: {
    transform: "rotate(180deg)",
  },

  image: {
    width: "100%",
    height: "auto",
  },
  img: {
    margin: "auto",
    display: "block",
    maxWidth: "100%",
    maxHeight: "100%",
  },

  testingList: {
    width: "100%",
    maxWidth: 360,
  },
}));

export default function TestedPositive() {
  const classes = useStyles();
  const [expanded, setExpanded] = React.useState(false);

  const handleExpandClick = () => {
    setExpanded(!expanded);
  };

  return (
    <Card className={classes.root}>
      <CardHeader
        avatar={
          <Avatar variant="rounded" alt="hosiptalIcon" src={hospitalIcon} />
        }
        action={
          <IconButton
            className={clsx(classes.expand, {
              [classes.expandOpen]: expanded,
            })}
            onClick={handleExpandClick}
            aria-expanded={expanded}
            aria-label="show more"
          >
            <ExpandMoreIcon />
          </IconButton>
        }
        title="What to do if you are tested positive?"
        titleTypographyProps={{ variant: "h6" }}
        className={classes.cardHeader}
      />

      <CardContent>
        <Typography className={classes.body}>
          The Health Ministry has designated 26 hospitals nationwide that will
          serve as referral hospitals in handling cases of patients under
          investigation (PUI) and those who tested positive. These hospitals
          will provide treatment to patients with no cost.
        </Typography>
      </CardContent>

      <Collapse in={expanded} timeout="auto" unmountOnExit>
        <CardContent>
          <CardMedia className={classes.image}>
            <img
              className={classes.img}
              alt="hosptreatment"
              src={hosptreatment}
            />
          </CardMedia>
        </CardContent>
      </Collapse>
    </Card>
  );
}
