import React, { Fragment, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemText from "@material-ui/core/ListItemText";
import Avatar from "@material-ui/core/Avatar";
import Box from "@material-ui/core/Box";
import Typography from "@material-ui/core/Typography";
import FavoriteIcon from "@material-ui/icons/Favorite";
import Chip from "@material-ui/core/Chip";
import FavoriteOutlinedIcon from "@material-ui/icons/FavoriteBorderOutlined";
import PersonIcon from "@material-ui/icons/PersonOutlined";
import BookmarkIcon from "@material-ui/icons/BookmarkBorderOutlined";
import VerifiedUserIcon from "@material-ui/icons/VerifiedUserOutlined";
import SettingsIcon from "@material-ui/icons/SettingsOutlined";
import ErrorOutlineOutlinedIcon from "@material-ui/icons/ErrorOutlineOutlined";
import Tooltip from "@material-ui/core/Tooltip";
import Badge from "@material-ui/core/Badge";
import AddAPhotoIcon from "@material-ui/icons/AddAPhoto";
import CircularProgress from '@material-ui/core/CircularProgress';

import { connect } from "react-redux";
import PropTypes from "prop-types";
import axios from "axios";


import { changeProfilePic } from "../../redux/actions/userActions";

const proxyurl = "https://cors-anywhere.herokuapp.com/";


const useStyles = makeStyles((theme) => ({
  root: {
    width: "100%",
    height: "100%",
    minHeight: "100vh",
    [theme.breakpoints.down("sm")]: {
      "& .MuiListItem-root": {
        borderBottom: "10px solid rgba(237, 235, 237, 0.4)",
        backgroundColor: "rgba(255, 255, 255, 100)",
      },
      backgroundColor: "rgba(237, 235, 237, 0.4)",
    },

    [theme.breakpoints.up("md")]: {
      borderRight: "1px solid rgba(237, 235, 237, 1)",
    },
  },

  img: {
    width: theme.spacing(12),
    height: theme.spacing(12),
    objectFit: "contain",
  },

  likeBox: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "center",
    alignItems: "center",
  },

  tag: {
    margin: theme.spacing(0.4),
  },

  tagBox: {
    display: "flex",
    justifyContent: "center",
    flexWrap: "wrap",
    listStyle: "none",
    padding: theme.spacing(0.5),
    [theme.breakpoints.down("xs")]: {
      marginLeft: 10,
    },
    marginBottom: 20,
  },
}));

//Doctor Account 左边的navigation
function DocSideNav(props) {
  const classes = useStyles();


  // determine if useEffect will be called: don't call if renderCount = 1
  const [renderCount, setRenderCount] = React.useState(0);


  const [userInfo, setState] = React.useState({
    profilePic: "",
    tags: [],
    username: "",
    likes: 0
  })



  // initial render: only gets called once
  useEffect(() => {
    // console.log(renderCount)
    if (!userInfo.username) {
      // console.log("fetching data .......")
      return displayStoredData();
    }
  });

  // const displayStoredData = () => {
  //   // call function to get data from returned props from firebase
  //   getStoredCredentials()
  //     .then((res) => {
  //       // console.log(res)

  //       let storedTags;

  //       if (!res[0]) {
  //         storedTags = "";
  //       } else {
  //         if (!res[0].tags) {
  //           storedTags = "";
  //         } else {
  //           storedTags = res[0].tags;
  //         }
  //       }

  //       setState({
  //         profilePic: res[1].imgSrc,
  //         tags: storedTags,
  //         username: res[1].username,
  //         likes: res[0].likes ? res[0].likes : 0
  //       })

  //       setRenderCount(1);
  //     }).catch((error) => {
  //       console.error(error);
  //     })
  // }

  // wait for returned props from firebase to be ready
  // let getStoredCredentials = async () => {
  //   let [storedSearchInfo, userStoredCredentials, user] =
  //     await Promise.all([props.searchInfo, props.storedCredentials, props.user]);
  //   // console.log([storedSearchInfo, userStoredCredentials])
  //   return [storedSearchInfo, userStoredCredentials, user];
  // }


  let returnedInfo = [];

  const displayStoredData = () => {
    fetchData()
      .then((res) => {
        if (res) {
          returnedInfo.push(res);
        }


        let credentials = returnedInfo[0].data.credentials;
        let username = credentials.username;

        let storedTags;
        let likes;

        // if we did not go into search data --> no specialty exists
        if (returnedInfo.length == 1) {
          storedTags = "";
          likes = 0;
        } else {
          let specialty = credentials.profile.specialty;
          let hospital = credentials.profile.hospital.replace(/\s+/g, "");

          let doctorInfo = returnedInfo[1].data[specialty].hospitals[hospital].doctors[username];
          storedTags = doctorInfo.tags ? doctorInfo.tags : "";
          likes = doctorInfo.likes ? doctorInfo.likes : 0;
        }


        setState({
          profilePic: credentials.imgSrc,
          tags: storedTags,
          username: username,
          likes: likes
        })

        setRenderCount(1);
      }).catch((error) => {
        console.error(error);
      })
  }


  // overall fetching function
  let fetchData = async () => {
    // wait for the first async function to finish before peoceeding
    return await getStoredCredentials()
      .then((res) => {
        // add result from the first async function to returnedInfo
        returnedInfo.push(res);
        // check to see if a field returned from the first async function exists
        if (res.data.credentials.profile.specialty) {
          // if yes, use it to proceed the second async function
          return getSpecialtyData(res.data.credentials.profile.specialty);
        }
      });
  }


  let getStoredCredentials = async () => {
    let userStoredCredentials = await axios.get(proxyurl + axios.defaults.baseURL + "user");
    return userStoredCredentials;
  }


  let getSpecialtyData = async (searchSpecialty) => {
    let specialtyData =
      await axios.get(proxyurl + axios.defaults.baseURL + "getallsearchdata",
        {
          params: {
            specialty: searchSpecialty
          }
        });
    return specialtyData;
  }




  // reads new image and sets state
  const uploadProfilePicture = (event) => {
    let reader = new FileReader();
    let uploaded = event.target.files[0];
    if (uploaded) {
      reader.readAsDataURL(uploaded);
    }
    reader.onload = () => {
      setState({
        ...userInfo,
        profilePic: reader.result
      })
    };
  };



  // wait for the newly uploaded image to be updated to state and post to firebase
  useEffect(() => {
    if (renderCount == 1) {
      // console.log("changed, sending...")
      return postProfilePic();
    }
  }, [userInfo.profilePic]);

  const postProfilePic = () => {
    waitProfileUpdate()
      .then((imgSrc) => {
        const imgSrcVar = {
          imgSrc: imgSrc
        }
        props.changeProfilePic(imgSrcVar);
      }).catch((error) => {
        console.error(error);
      });
  }

  let waitProfileUpdate = async () => {
    let newProfilePic = await userInfo.profilePic;
    return newProfilePic;
  }

  // create a tag list that contains all tags for this doctor
  let tagList = (userInfo.tags) ? userInfo.tags.map(tag => {
    return (
      <Fragment key={tag.tagName}>
        <li key={tag.tagName}>
          <Chip
            label={tag.tagName + ' (' + tag.count + ')'}
            className={classes.tag}
            color="secondary"
            size="small"
          />
        </li>
      </Fragment>
    )
  }) : <p>No review tags yet</p>;








  //选 list item (profile, saved, like history, acc verification, acc settings)
  const [selectedIndex, setSelectedIndex] = React.useState();

  const handleListItemClick = (event, index) => {
    setSelectedIndex(index);
  };

  //tooltip first time user instruction
  const [open, setOpen] = React.useState(false);
  const handleTooltipClose = () => {
    setOpen(false);
  };
  const handleTooltipOpen = () => {
    setOpen(true);
  };

  // console.log(props.storedCredentials)

  if (renderCount == 0) {
    // if (!userInfo.username) {
    // loading spinner if prop data is not yet available
    return (
      <div>
        <CircularProgress color="secondary" style={{ marginLeft: '45%', marginTop: '5%' }} />
      </div>
    );

  } else {


    return (
      <Fragment>
        <div className={classes.root}>
          <List component="nav">
            <Box
              display="flex"
              flexDirection="column"
              justifyContent="center"
              alignItems="center"
              mt={3}
            >
              {/* User profile image */}
              <div className={classes.uploadImg}>
                <input
                  accept="image/*"
                  style={{ display: "none" }}
                  id="contained-button-profile-pic"
                  multiple
                  type="file"
                  onChange={uploadProfilePicture}
                />
                <label htmlFor="contained-button-profile-pic">
                  <Badge
                    style={{ cursor: "pointer" }}
                    overlap="circle"
                    anchorOrigin={{
                      vertical: "bottom",
                      horizontal: "right",
                    }}
                    badgeContent={<AddAPhotoIcon />}
                  >
                    <Avatar
                      className={classes.img}
                      alt="doctor profile picture"
                      src={userInfo.profilePic}
                    />
                  </Badge>
                </label>
              </div>

              <Typography variant="h6" color="primary">
                Hi, {userInfo.username}!
            </Typography>

              <Box className={classes.likeBox} mt={2} mb={2}>
                {/* Likes */}
                <FavoriteIcon style={{ color: "red" }} />
                <Typography variant="body2" color="primary">
                  &nbsp;{userInfo.likes}
                </Typography>
              </Box>
              {/* Tags */}
              <div className={classes.tagBox}>
                {tagList}
                {/* 这边需要麻烦你写, 如果没有tag，要显示‘no review tags'， 有tag的话这个就要藏起来 */}

                {/* <p>No review tags yet</p> */}
              </div>
            </Box>

            {/* 目前写法是点了一个list item, 就跳到属于它的section, */}
            {/* Profile */}
            <ListItem
              button
              selected={selectedIndex === 0}
              onClick={(event) => handleListItemClick(event, 0)}
              component="a"
              href="/profile"
            >
              <ListItemIcon style={{ marginLeft: 20 }}>
                <PersonIcon />
              </ListItemIcon>
              <ListItemText primary="Profile" />
              {/* 第一次user要显示这个tooltip */}
              <Tooltip
                disableFocusListener
                placement="right"
                title={
                  <h2 style={{ fontWeight: "normal", lineHeight: 1.5 }}>
                    2. Set up your profile <br></br>
                    <br></br>
                    Add your personal, work, and expertise information.
                </h2>
                }
              >
                <ErrorOutlineOutlinedIcon style={{ color: "red" }} />
              </Tooltip>
            </ListItem>

            {/* Saved  */}
            <ListItem
              button
              selected={selectedIndex === 1}
              onClick={(event) => handleListItemClick(event, 1)}
              component="a"
              href="/saved"
            >
              <ListItemIcon style={{ marginLeft: 20 }}>
                <BookmarkIcon />
              </ListItemIcon>
              <ListItemText primary="Saved" />
            </ListItem>

            {/* Like History */}
            <ListItem
              button
              selected={selectedIndex === 2}
              onClick={(event) => handleListItemClick(event, 2)}
              component="a"
              href="/likehistory"
            >
              <ListItemIcon style={{ marginLeft: 20 }}>
                <FavoriteOutlinedIcon />
              </ListItemIcon>
              <ListItemText primary="Like History" />
            </ListItem>

            {/* Account verification */}
            <ListItem
              button
              selected={selectedIndex === 3}
              onClick={(event) => handleListItemClick(event, 3)}
              style={{ position: "relative" }}
              component="a"
              href="/accountverification"
            >
              <ListItemIcon style={{ marginLeft: 20 }}>
                <VerifiedUserIcon />
              </ListItemIcon>
              <ListItemText primary="Account Verification" />
              {/* 第一次user要显示这个tooltip */}

              <Tooltip
                disableFocusListener
                placement="right"
                title={
                  <h2 style={{ fontWeight: "normal", lineHeight: 1.5 }}>
                    Welcome Alex! Before you start: <br></br>
                    <br></br>
                    1. Verify your account <br></br>
                    <br></br>
                    Once you verified your account, you will be able to publish
                    your profile.
                </h2>
                }
              >
                <ErrorOutlineOutlinedIcon style={{ color: "red" }} />
              </Tooltip>
            </ListItem>

            {/* Account settings */}
            <ListItem
              button
              selected={selectedIndex === 4}
              onClick={(event) => handleListItemClick(event, 4)}
              component="a"
              href="/accountsettings"
            >
              <ListItemIcon style={{ marginLeft: 20 }}>
                <SettingsIcon />
              </ListItemIcon>
              <ListItemText primary="Account Settings" />
            </ListItem>
          </List>
        </div>
      </Fragment>
    );
  }
