import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import Hidden from "@material-ui/core/Hidden";

//components from docAccount folder
import DocSideNav from "../Components/docAccount/DocSideNav";
import Navbar from "../Components/Navbar";
import CovidAlert from "../Components/Alert";
import DocEditProfile from "../Components/docAccount/DocEditProfile";
import DocAccountVerification from "../Components/docAccount/DocAccountVerification";
import AccountSettings from "../Components/docAccount/AccountSettings";
import DocCard from "../Components/results/DocCard";

// material ui style
const useStyles = makeStyles((theme) => ({
  covidBox: {
    marginTop: 64,
    [theme.breakpoints.down("sm")]: {
      marginTop: 20,
      marginLeft: 20,
      marginRight: 20,
    },
  },

  // sideNav: {
  //   [theme.breakpoints.down("sm")]: {
  //     display: none,
  //   },
  // }
}));

function DocAccount() {
  const classes = useStyles();

  const [tabIndex, setTabIndex] = React.useState(0);

  const getSelectedIndex = (value) => {
    setTabIndex(value);
  };


  let rightPanel = <DocEditProfile getSelectedIndex={getSelectedIndex}/>;
  if (tabIndex == 0) {
    rightPanel = <DocEditProfile getSelectedIndex={getSelectedIndex}/>;
  } else if (tabIndex == 1) {
    rightPanel = "";
  } else if (tabIndex == 2) {
    rightPanel = "";
  } else if (tabIndex == 3) {
    rightPanel = <DocAccountVerification getSelectedIndex={getSelectedIndex}/>;
  } else {
    rightPanel = <AccountSettings getSelectedIndex={getSelectedIndex}/>;
  }

  return (
    <div>
      <Navbar />
      <Hidden smDown>
        <div className={classes.covidBox} style={{ position: "relative" }}>
          <CovidAlert />

          <Grid container spacing={0}>
            <Grid item md={4} lg={3}>
              <DocSideNav/>
            </Grid>

            <Grid item sm={12} md={8} lg={9}>
              {/* <DocEditProfile getSelectedIndex={getSelectedIndex}/> */}
              {rightPanel}
              {/* <DocAccountVerification /> */}
              {/* <AccountSettings /> */}
              {/* <DocCard /> */}
            </Grid>
          </Grid>
        </div>
      </Hidden>
      <Hidden mdUp>
        <DocSideNav />
        {/* <DocEditProfile /> */}
        {/* <DocAccountVerification /> */}
        {/* <AccountSettings /> */}
        {/* <DocCard /> */}
      </Hidden>
    </div>
  );
}

export default DocAccount;
