import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import clsx from "clsx";
import Card from "@material-ui/core/Card";
import CardHeader from "@material-ui/core/CardHeader";
import CardContent from "@material-ui/core/CardContent";
import Collapse from "@material-ui/core/Collapse";
import Avatar from "@material-ui/core/Avatar";
import IconButton from "@material-ui/core/IconButton";
import Typography from "@material-ui/core/Typography";
import preventionIcon from "../../img/covid/preventionIcon.png";

import ExpandMoreIcon from "@material-ui/icons/ExpandMore";
const useStyles = makeStyles((theme) => ({
  root: {
    width: "100%",
    marginBottom: 40,
  },
  cardHeader: {
    color: "#003367",
    fontFamily: "'Raleway', sans-serif",
  },

  body: {
    fontFamily: '"Montserrat", sans-serif',
  },

  expand: {
    transform: "rotate(0deg)",
    marginLeft: "auto",
    transition: theme.transitions.create("transform", {
      duration: theme.transitions.duration.shortest,
    }),
  },
  expandOpen: {
    transform: "rotate(180deg)",
  },
}));

export default function Covid() {
  const classes = useStyles();
  const [expanded, setExpanded] = React.useState(false);

  const handleExpandClick = () => {
    setExpanded(!expanded);
  };

  return (
    <Card className={classes.root}>
      <CardHeader
        avatar={<Avatar alt="img" src={preventionIcon} />}
        action={
          <IconButton
            className={clsx(classes.expand, {
              [classes.expandOpen]: expanded,
            })}
            onClick={handleExpandClick}
            aria-expanded={expanded}
            aria-label="show more"
          >
            <ExpandMoreIcon />
          </IconButton>
        }
        title="Prevention"
        titleTypographyProps={{ variant: "h6" }}
        className={classes.cardHeader}
      />

      <CardContent>
        <Typography className={classes.body}>
          There is currently no vaccine to protect against COVID-19. To lower
          the risk of getting the disease, you can:
        </Typography>{" "}
        <Avatar alt="img" src={preventionIcon} />
        <Typography className={classes.body}>
          <strong>Wash your hands frequently</strong>
          Regularly and thoroughly clean your hands with an alcohol-based hand
          rub or wash them with soap and water.
        </Typography>
      </CardContent>

      <Collapse in={expanded} timeout="auto" unmountOnExit>
        <CardContent>
          <Typography className={classes.body}>
            The virus probably emerged from an animal source, it spreads mainly
            between people who are in close contact with each other when an
            infected person coughs or sneezes. It is also possible for a person
            to get COVID-19 by touching an object that has the virus on it and
            then touching their mouths, nose or eyes.
          </Typography>
        </CardContent>
      </Collapse>
    </Card>
  );
}
