import React, { Fragment, useEffect } from "react";
import { connect } from "react-redux";
import { makeStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import Box from "@material-ui/core/Box";
import Typography from "@material-ui/core/Typography";
import Chip from "@material-ui/core/Chip";
import Grid from "@material-ui/core/Grid";
import IconButton from "@material-ui/core/IconButton";
import Hidden from "@material-ui/core/Hidden";

//icons
import ArrowBackIosIcon from "@material-ui/icons/ArrowBackIos";
import LocationOnOutlinedIcon from "@material-ui/icons/LocationOnOutlined";
import PhoneOutlinedIcon from "@material-ui/icons/PhoneOutlined";
import ErrorIcon from '@material-ui/icons/Error';
import ErrorOutlineOutlinedIcon from "@material-ui/icons/ErrorOutlineOutlined";
import BookmarkIcon from '@material-ui/icons/Bookmark';
import BookmarkBorderOutlinedIcon from "@material-ui/icons/BookmarkBorderOutlined";
import ShareOutlinedIcon from "@material-ui/icons/ShareOutlined";
import FavoriteIcon from "@material-ui/icons/Favorite";
import FavoriteBorderOutlinedIcon from "@material-ui/icons/FavoriteBorderOutlined";
import EditIcon from '@material-ui/icons/Edit';
import EditOutlinedIcon from "@material-ui/icons/EditOutlined";

import Dialog from "@material-ui/core/Dialog";
import DialogContent from "@material-ui/core/DialogContent";
import DialogTitle from "@material-ui/core/DialogTitle";
import DialogActions from "@material-ui/core/DialogActions";
import TextField from "@material-ui/core/TextField";
import CloseIcon from "@material-ui/icons/Close";
import { Link } from "react-router-dom";

import PropTypes from "prop-types";

import { changeDocLikeStatus, changeDocSaveStatus, sendReportedDoctors } from "../../redux/actions/userActions";
import { updateDoctorLikes, report } from "../../redux/actions/dataActions";

//components
import CovidAlert from "../Alert";
import DocTags from "./DocTags";

const useStyles = makeStyles((theme) => ({
  covidBox: {
    marginTop: 64,
    [theme.breakpoints.down("sm")]: {
      marginTop: 20,
      marginLeft: 20,
      marginRight: 20,
    },
  },

  //return to doctors button
  returnBox: {
    marginLeft: 50,
    [theme.breakpoints.down("sm")]: {
      marginLeft: 20,
    },
  },

  //doctor image
  img: {
    width: "100%",
    height: "100%",
    objectFit: "contain",
  },

  profileGrid: {
    [theme.breakpoints.down("xs")]: {
      marginLeft: 20,
      marginRight: 20,
    },
  },

  line: {
    [theme.breakpoints.down("xs")]: {
      border: "5px solid rgba(0, 0, 0, 0.05)",
    },
  },

  reportButton: {
    marginRight: "1rem",
  }
}));


//for Doc image + basic info + likes
//edit tags section is imported from DocTags.js

function DocInfo(props) {
  const classes = useStyles();

  const backToRes = () => {
    if (props.history != null) {
      if (props.backTo == "resultsPage") {
        props.history.push("/results");
      } else if (props.backTo == "hospprofile") {
        props.history.push("/hospprofile");
      }
    }
  };


  const authenticated = props.authenticated;

  const [renderCount, setRenderCount] = React.useState(0);

  const [likeSaveInfo, setState] = React.useState({
    // like functionality
    hasLiked: false,
    likedList: [],
    numLikes: 0,

    // save functionality
    hasSaved: false,
    savedList: [],

    // report functionality
    reportedList: [],
    numReports: 0,
    reportReasonsList: [],
    oneReason: ""
  });

  // only get called once in the first render
  useEffect(() => {
    if (renderCount == 0) {
      return displayStoredData();
    }
  });

  const displayStoredData = () => {
    getStoredData()
      .then((res) => {
        // like
        let listOfLikes;
        let liked = false;
        // if the user has never liked any doctors
        if (!res[1].likeHistory) {
          listOfLikes = [];
        } else {
          if (res[1].likeHistory.doctors) {
            listOfLikes = res[1].likeHistory.doctors;
            // if the user has liked this particular doctor before
            const index = res[1].likeHistory.doctors.findIndex(doctor => doctor.username.replace(" ", "").toLowerCase() == props.targetDoc.userName.replace(" ", "").toLowerCase())
            if (index != -1) {
              liked = true;
            }
          } else {
            listOfLikes = [];
          }
        }

        // save
        let listOfSaves;
        let saved = false;

        // if the user has never saved any doctors
        if (!res[1].saved) {
          listOfSaves = [];
        } else {
          if (res[1].saved.doctors) {
            listOfSaves = res[1].saved.doctors;
            // if the user has saved this particular doctor before
            const index = res[1].saved.doctors.findIndex(doctor => doctor.username.replace(" ", "").toLowerCase() == props.targetDoc.userName.replace(" ", "").toLowerCase())
            if (index != -1) {
              saved = true;
            }
          } else {
            listOfSaves = [];
          }
        };


        setState({
          hasLiked: liked,
          likedList: listOfLikes,
          numLikes: res[0].likes ? res[0].likes : 0,

          hasSaved: saved,
          savedList: listOfSaves,

          reportedList: res[1].reportedDoctors ? res[1].reportedDoctors : [],
          numReports: res[0].report ? res[0].report.reportCount : 0,
          reportReasonsList: res[0].report ? res[0].report.reportReasons : [],
          oneReason: ""
        });

        setRenderCount(1);
