import React from "react";
import clsx from "clsx";
import Box from "@material-ui/core/Box";
import { makeStyles } from "@material-ui/core/styles";
import Drawer from "@material-ui/core/Drawer";
import Button from "@material-ui/core/Button";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";

//filter functions
import {
  HospitalType,
  Languages,
  YearsOfPractice,
  Location,
} from "./FiltersFunctions";

const useStyles = makeStyles((theme) => ({
  //displayby
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
  //filters
  list: {
    width: 250,
  },
  fullList: {
    width: "auto",
  },
}));

// displayby dropdown for small screens
export function DisplayByPhone() {
  const classes = useStyles();
  const [choice, setChoice] = React.useState("doctor");

  const handleChange = (event) => {
    setChoice(event.target.value);
  };

  return (
    <div>
      <FormControl variant="outlined" className={classes.formControl}>
        <InputLabel>Display by</InputLabel>
        <Select value={choice} onChange={handleChange} label="displayby">
          <MenuItem value="doctor">Doctor</MenuItem>
          <MenuItem value="hospital">Hospital</MenuItem>
        </Select>
      </FormControl>
    </div>
  );
}

//filter button for small screens
export function FilterButtonPhone() {
  const classes = useStyles();
  const [state, setState] = React.useState({
    left: false,
  });

  const toggleDrawer = (anchor, open) => (event) => {
    if (
      event.type === "keydown" &&
      (event.key === "Tab" || event.key === "Shift")
    ) {
      return;
    }
    setState({ ...state, [anchor]: open });
  };

  const list = (anchor) => (
    <div
      className={clsx(classes.list, {
        [classes.fullList]: anchor === "top" || anchor === "bottom",
      })}
      role="presentation"
      onClick={toggleDrawer(anchor, false)}
      onKeyDown={toggleDrawer(anchor, false)}
    >
      <Box display="flex" flexDirection="column" justifyContent="center" ml={8}>
        <br></br>
        <br></br>
        <HospitalType />
        <br></br>
        <br></br>
        <Languages />
        <br></br>
        <br></br>
        {/* If display by doctor, filter sidebar will show years of practice; 
            if display by hospital, filter sidebar will show location */}
        {choice === "doctor" ? <YearsOfPractice /> : <Location />}
      </Box>
    </div>
  );

  return (
    <div>
      {["Filter"].map((anchor) => (
        <React.Fragment key={anchor}>
          <Button
            variant="outlined"
            color="primary"
            onClick={toggleDrawer(anchor, true)}
          >
            {anchor}
          </Button>
          <Drawer
            anchor={anchor}
            open={state[anchor]}
            onClose={toggleDrawer(anchor, false)}
          >
            {list(anchor)}
          </Drawer>
        </React.Fragment>
      ))}
    </div>
  );
}
