import React, { Fragment } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Drawer from "@material-ui/core/Drawer";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";

import CovidAlert from "../Alert";
import Box from "@material-ui/core/Box";
import Radio from "@material-ui/core/Radio";
import RadioGroup from "@material-ui/core/RadioGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import FormControl from "@material-ui/core/FormControl";
import FormLabel from "@material-ui/core/FormLabel";
import FormGroup from "@material-ui/core/FormGroup";
import ToggleButton from "@material-ui/lab/ToggleButton";
import ToggleButtonGroup from "@material-ui/lab/ToggleButtonGroup";

import Checkbox from "@material-ui/core/Checkbox";
import Grid from "@material-ui/core/Grid";
import Pagination from "@material-ui/lab/Pagination";

import DocCard from "./DocCard";
import { HospitalType, Languages, YearsOfPractice } from "./Filters";
const drawerWidth = 240;

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
    height: "100vh",
  },
  appBar: {
    zIndex: theme.zIndex.drawer + 1,
  },
  drawer: {
    width: drawerWidth,
    flexShrink: 0,
  },
  drawerPaper: {
    width: drawerWidth,
  },
  drawerContainer: {
    overflow: "auto",
  },

  content: {
    flexGrow: 1,
    padding: theme.spacing(3),
  },

  toggleContainer: {
    margin: theme.spacing(3, 0),
  },
}));

export default function SearchResultsFilter() {
  const classes = useStyles();

  //display by doctor/hospital
  const [choice, setChoice] = React.useState("doctor");
  const handleChoice = (event, newChoice) => {
    setChoice(newChoice);
  };

  //pagination
  const [page, setPage] = React.useState(1);
  const handleChange = (event, value) => {
    setPage(value);
  };

  return (
    <div className={classes.root}>
      <Drawer
        className={classes.drawer}
        variant="permanent"
        classes={{
          paper: classes.drawerPaper,
        }}
      >
        <Toolbar />
        <div className={classes.drawerContainer}>
          <Box
            display="flex"
            flexDirection="column"
            justifyContent="center"
            ml={8}
          >
            <br></br>
            <br></br>

            <HospitalType />
            <br></br>
            <br></br>

            <Languages />
            <br></br>
            <br></br>
            <YearsOfPractice />
          </Box>
        </div>
      </Drawer>
      <main className={classes.content}>
        <Toolbar />
        <div
          className={classes.covidBox}
          style={{ marginLeft: 30, marginRight: 30 }}
        >
          <CovidAlert />
        </div>
        <Grid container spacing={0}>
          <Grid item sm={9} lg={10}>
            {choice === "doctor" ? (
              <Typography
                style={{ marginLeft: 30, marginTop: 30, marginBottom: 30 }}
                variant="h6"
                color="primary"
              >
                Display results by doctors
              </Typography>
            ) : (
              <Typography
                style={{ marginLeft: 30, marginTop: 30, marginBottom: 30 }}
                variant="h6"
                color="primary"
              >
                Display results by hospitals
              </Typography>
            )}
          </Grid>
          <Grid item sm={3} lg={2}>
            <Fragment>
              <div className={classes.toggleContainer}>
                <ToggleButtonGroup
                  value={choice}
                  exclusive
                  onChange={handleChoice}
                >
                  <ToggleButton value="doctor">
                    <Typography
                      color="primary"
                      style={{ textTransform: "none" }}
                    >
                      Doctor
                    </Typography>
                  </ToggleButton>

                  <ToggleButton value="hospital">
                    <Typography
                      color="primary"
                      style={{ textTransform: "none" }}
                    >
                      Hospital
                    </Typography>
                  </ToggleButton>
                </ToggleButtonGroup>
              </div>
            </Fragment>
          </Grid>
        </Grid>
        <div>
          {choice === "doctor" ? (
            <Fragment>
              <DocCard />
              <DocCard />
              <DocCard />
              <DocCard />
              <DocCard />
              <DocCard />
              <DocCard />
              <DocCard />
            </Fragment>
          ) : (
            "hello"
          )}
        </div>
        <Box display="flex" justifyContent="center">
          <Pagination
            count={10}
            page={page}
            onChange={handleChange}
            shape="rounded"
            color="primary"
          />
        </Box>
      </main>
    </div>
  );
}
