import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";
import Box from "@material-ui/core/Box";
import Divider from "@material-ui/core/Divider";
import Hidden from "@material-ui/core/Hidden";
import IconButton from "@material-ui/core/IconButton";
import NavigateBeforeIcon from "@material-ui/icons/NavigateBefore";
import NavigateNextIcon from "@material-ui/icons/NavigateNext";

//components
import TopRatedDocCard from "./TopRatedDocCard";

const useStyles = makeStyles((theme) => ({
  divider: {
    height: 2,
    backgroundColor: "#FF8686",
  },

  line: {
    border: "0.5px solid rgba(0, 0, 0, 0.2)",
    [theme.breakpoints.down("xs")]: {
      border: "5px solid rgba(0, 0, 0, 0.05)",
    },
  },

  navigateIcon: {
    color: "003367",
    fontSize: 60,
    [theme.breakpoints.down("xs")]: {
      fontSize: 25,
    },
  },

  topRatedDocBox: {
    [theme.breakpoints.up("sm")]: {
      justifyContent: "center",
      alignItems: "center",
    },
    [theme.breakpoints.down("sm")]: {
      justifyContent: "left",
      alignItems: "left",
      marginLeft: 20,
    },
  },

  thinLine: {
    border: "0.5px solid rgba(0, 0, 0, 0.2)",
    [theme.breakpoints.down("xs")]: {
      border: "5px solid rgba(0, 0, 0, 0.05)",
    },
  },
}));

// for Top Rated Doctors, Top Rated Specialty, Insurance Accepted section (used in HospProfile.js under pages folder)
export default function DocInfo() {
  const classes = useStyles();



    // create procedure List
    let Specialities = props.targetHos['Top'].map((proc)=>{
      let procCard = <p>{proc}</p>;
      return procCard;
    })

    // create condition List
    let Insurance = props.targetHos['Conditions'].map((condition)=>{
      let conditionCard = <p>{condition}</p>;
      return conditionCard;
    })

  return (
    <Grid container spacing={0}>
      <Grid item xs={12}>
        <Box
          display="flex"
          flexDirection="column"
          mb={2}
          mt={2}
          className={classes.topRatedDocBox}
        >
          <Typography variant="h6" color="primary">
            Top Rated Doctors
          </Typography>
          <Divider className={classes.divider} style={{ width: 170 }} />
          <br></br>
          <br></br>
          {/* 这里出现top rated doctors 的卡片，目前点左右button是不会有反应的，麻烦你了 */}
          <Grid container spacing={0} alignItems="center" justify="center">
            {/* navigate left button */}
            <Grid item xs={2} md={2} align="center">
              <IconButton>
                <NavigateBeforeIcon className={classes.navigateIcon} />
              </IconButton>
            </Grid>
            {/* 最左边第一张卡片 */}
            <Grid item xs={8} sm={4} md={2} align="center">
              <TopRatedDocCard />
            </Grid>
            <Hidden xsDown>
              {/* 第一张与第二张卡片之间的空格 */}
              <Grid item md={1} />
              {/* 中间卡片，这个在最小手机屏幕是不会出现的 */}
              <Grid item md={2}>
                <TopRatedDocCard />
              </Grid>
            </Hidden>
            <Hidden smDown>
              {/* 第二张与第三张卡片之间的空格 */}
              <Grid item md={1} />
              {/* 最右边卡片，这个在中屏幕以下是不会出现的 */}
              <Grid item sm={4} md={2}>
                <TopRatedDocCard />
              </Grid>
            </Hidden>
            {/* navigate right button */}
            <Grid item xs={2} md={2} align="center">
              <IconButton>
                <NavigateNextIcon className={classes.navigateIcon} />
              </IconButton>
            </Grid>
          </Grid>
        </Box>
        <br></br>
        <hr className={classes.thinLine}></hr>
      </Grid>
      {/* 以下是top rated specialities + accepted insurance section */}
      <Grid container spacing={0}>
        <Grid item xs={0} sm={1}></Grid>
        <Grid item xs={12} sm={4}>
          <Box
            display="flex"
            flexDirection="column"
            justifyContent="center"
            mb={2}
            ml={2}
            mt={2}
          >
            {/* top rated specialties */}
            <Typography variant="h6" color="primary">
              Top Rated Specialities
            </Typography>
            <Divider className={classes.divider} style={{ width: 200 }} />
            <Typography variant="body1">
              <p>Neurology (134,334)</p>
              <p>Neurology (134,334)</p>
              <p>Neurology (134,334)</p>
              <p>Neurology (134,334)</p>
              <p>Neurology (134,334)</p>
            </Typography>
          </Box>
          <Hidden smUp>
            <hr className={classes.line}></hr>
          </Hidden>
        </Grid>
        <Grid item xs={0} sm={2}></Grid>
        {/* insurance accepted */}
        <Grid item xs={12} sm={4}>
          <Box
            display="flex"
            flexDirection="column"
            justifyContent="center"
            mb={2}
            ml={2}
            mt={2}
          >
            <Typography variant="h6" color="primary">
              Insurance Accepted
            </Typography>
            <Divider className={classes.divider} style={{ width: 180 }} />
            <Typography variant="body1">
              <p>Asia Assistance Network</p>
              <p>AIA</p>
              <p>AIA - Employee Benefit</p>
              <p>AIA Datalink</p>
              <p>Great Eastern</p>
            </Typography>
          </Box>
        </Grid>
      </Grid>
    </Grid>
  );
}
