import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import Box from "@material-ui/core/Box";
import solution1 from "../../img/home/solution1.png";
import solution2 from "../../img/home/solution2.png";
import solution3 from "../../img/home/solution3.png";
import linearrow1 from "../../img/home/linearrow1.png";
import linearrow2 from "../../img/home/linearrow_2.png";

import { Typography } from "@material-ui/core";
import Divider from "@material-ui/core/Divider";
import Hidden from "@material-ui/core/Hidden";

const useStyles = makeStyles((theme) => ({
  ...theme.home,
  container: {
    marginTop: 120,
    backgroundColor: "#fff",
  },
  root: {
    flexGrow: 1,
  },

  grid: {
    marginTop: 30,
  },

  heading: {
    [theme.breakpoints.down("sm")]: {
      fontSize: 24,
    },
    fontSize: 28,
  },

  solutionImg: {
    marginLeft: 20,
    marginRight: 20,
    width: "20%",
    height: "20%",
  },

  solutionTitle: {
    marginRight: 40,
    marginLeft: 40,

    [theme.breakpoints.down("sm")]: {
      fontSize: 20,
    },
  },

  solutionBody: {
    marginRight: 60,
    lineHeight: 2,
    marginLeft: 60,
    color: "rgba(0, 0,0, 0.6)",
    marginBottom: 30,
    [theme.breakpoints.down("sm")]: {
      marginRight: 40,
      marginLeft: 30,
    },
  },
  highlightText: {
    color: "#FF8686",
  },

  lineArrow: {
    width: "20%",
    height: "20%",
  },
  lineArrow2: {
    width: "35%",
    height: "35%",
  },
}));

export default function Solution() {
  const classes = useStyles();

  return (
    <div className={classes.container}>
      <div className={classes.root}>
        <Box
          display="flex"
          flexDirection="column"
          alignItems="center"
          justifyContent="center"
        >
          <Divider className={classes.divider} />
          <br></br>
          <Typography
            color="primary"
            align="center"
            className={classes.heading}
          >
            Solution
          </Typography>
          <br></br>
        </Box>
        <Grid container spacing={0} className={classes.grid}>
          <Grid item xs={12} sm={4} className={classes.leftGrid}>
            <br></br>
            <Box
              display="flex"
              flexDirection="column"
              alignItems="center"
              justifyContent="center"
            >
              <img
                src={solution1}
                alt="solution1"
                className={classes.solutionImg}
              />

              <br></br>

              <Typography
                variant="body"
                align="center"
                className={classes.solutionBody}
              >
                Users discover doctors by specialty, condition, and location
              </Typography>
            </Box>
          </Grid>
          <Hidden xsDown>
            <Box position="absolute" zIndex="modal" left="28%">
              <img
                src={linearrow1}
                alt="linearrow1"
                className={classes.lineArrow}
              />
            </Box>
          </Hidden>

          <Grid item xs={12} sm={4} className={classes.rightGrid}>
            <br></br>
            <Box
              display="flex"
              flexDirection="column"
              alignItems="center"
              justifyContent="center"
              position="relative"
            >
              <img
                src={solution2}
                alt="solution2"
                className={classes.solutionImg}
              />

              <br></br>

              <Typography
                variant="body"
                align="center"
                className={classes.solutionBody}
              >
                We gather all doctor information in one place and show you
                people’s recommended doctors
              </Typography>
            </Box>
          </Grid>
          <Hidden xsDown>
            <Box position="absolute" zIndex="tooltip" left="56%">
              <img
                src={linearrow2}
                alt="linearrow2"
                className={classes.lineArrow2}
              />
            </Box>
          </Hidden>

          <Grid item xs={12} sm={4} className={classes.leftGrid}>
            <br></br>
            <Box
              display="flex"
              flexDirection="column"
              alignItems="center"
              justifyContent="center"
            >
              <img
                src={solution3}
                alt="solution3"
                className={classes.solutionImg}
              />

              <br></br>

              <Typography
                variant="body"
                align="center"
                className={classes.solutionBody}
              >
                Doctors present their services online and engage new patients
              </Typography>
            </Box>
          </Grid>
        </Grid>
      </div>
    </div>
  );
}
