import React, { Fragment } from "react";
import { makeStyles } from "@material-ui/core/styles";

import Dialog from "@material-ui/core/Dialog";
import Chip from "@material-ui/core/Chip";
import Button from "@material-ui/core/Button";
import Box from "@material-ui/core/Box";
import Typography from "@material-ui/core/Typography";
import DialogActions from "@material-ui/core/DialogActions";
import Hidden from "@material-ui/core/Hidden";
import Divider from "@material-ui/core/Divider";
import EditOutlinedIcon from "@material-ui/icons/EditOutlined";

import { connect } from "react-redux";
import PropTypes from "prop-types";

const useStyles = makeStyles((theme) => ({
  editTagDialog: {
    display: "flex",
    justifyContent: "center",
    flexWrap: "wrap",
    listStyle: "none",
    padding: theme.spacing(0.5),
    margin: 0,
  },
  tag: {
    margin: theme.spacing(0.5),
  },

  tagDialog: {
    display: "flex",
    flexWrap: "wrap",
    listStyle: "none",
    padding: theme.spacing(0.5),
    margin: 0,
    [theme.breakpoints.down("xs")]: {
      marginLeft: 10,
    },
    marginBottom: 20,
  },

  editTagsButton: {
    [theme.breakpoints.down("xs")]: {
      marginLeft: 20,
    },
  },

  line: {
    [theme.breakpoints.down("xs")]: {
      border: "5px solid rgba(0, 0, 0, 0.05)",
    },
  },
  reviewTagsBox: {
    [theme.breakpoints.down("xs")]: {
      justifyContent: "left",
      alignItems: "left",
      marginLeft: 20,
    },
  },
  divider: {
    height: 2,
    width: 115,
    backgroundColor: "#FF8686",
  },
}));




// Edit Tags (用在DocInfo.js)
// 目前doctor所有的tags and edit tags 都是hardcoded， 麻烦你了
function Tags(props) {

  const classes = useStyles();
  // load user authenticate 
  const authenticated = props.authenticated;

  let doctorTags = [];
  props.searchInfo.tags.forEach(tag => {
    doctorTags.push({
      tagName: tag.tagName,
      count: tag.count
    });
  });

  // const [currentTags, setCurrentTags] = React.useState(doctorTags);

  let userTags = [];
  props.storedCredentials.addedDoctorTags.verify.map(tag => {
    userTags.push(tag);
  });


  // const [storedUserTags, setStoredUserTags] = React.useState(userTags);

  const [allTags, setState] = React.useState({
    currentTags: doctorTags,
    storedUserTags: userTags
  })


  // 这是点进去edit tags 会出现的tags选项
  const [editTags, displayEditedTags] = React.useState([
    "Attentive",
    "Bedside manner",
    "Empathetic",
    { key: 3, label: "Enthusiastic" },
    { key: 4, label: "Friendly" },
    { key: 5, label: "Honest" },
    { key: 6, label: "Knowledgeable" },
    { key: 7, label: "Passionate" },
    { key: 8, label: "Patient" },
    { key: 9, label: "Respectful" },
    { key: 10, label: "Responsible" },
    { key: 11, label: "Trustworthy" },
  ]);

  const [open, setOpen] = React.useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = (value) => {
    setOpen(false);
  };

  // 这里我想说点了一个tag，就会换色（从outlined 换成default)
  // 但是我不会写那个logic,所以目前是点了一个tag,所有tag都一起换色了。。麻烦你了
  // 还有需要弄一个logic是 user再点一次又会换成原来outlined的style,麻烦你了
  // const [color, setColor] = React.useState("outlined");
  // const handleClick = () => {
  //   setColor("default");
  // };

  const addRemoveTag = (tag) => {
    let currentTagsCopy = allTags.currentTags;
    let currentTagIndex = currentTagsCopy.findIndex(eachTag => eachTag.tagName == tag)

    let userStoredTagsCopy = allTags.storedUserTags;
    let userStoredTagIndex = userStoredTagsCopy.indexOf(tag);

    // if the tag has been selected by user, which means it should also exist
    // in the database
    console.log(currentTagIndex)
    if (userStoredTagIndex !== -1) {
      // remove from user selected tag list in the modal
      userStoredTagsCopy.splice(userStoredTagIndex, 1);
      // decrease this tag's count for this doctor in database; this tag should
      // have already existed in the database since the user selected it
      currentTagsCopy[currentTagIndex].count--;
      if (currentTagsCopy[currentTagIndex].count == 0) {
        currentTagsCopy.splice(currentTagIndex, 1);
      }
      setState({
        currentTags: currentTagsCopy,
        storedUserTags: userStoredTagsCopy 
      })
      // the tag hasn't been selected by the user
    } else {
      // if the tag already exists in the database (other users had selected this
      //  tag for this doctor)
      if (currentTagIndex !== -1) {
        // increase the count for this tag in database by 1
        currentTagsCopy[currentTagIndex].count++;

        setState(prevState => ({
          currentTags: currentTagsCopy,
          // add the new tag to this user's tag list
          storedUserTags: [
            ...prevState.storedUserTags, tag
          ]
        }))
        // the tag doesn't exist in the database; this is the first time a user
        // selects this tag
      } else {
        setState(prevState => ({
          currentTags: [
            ...prevState.currentTags, { tagName: tag, count: 1 }
          ],
          storedUserTags: [
            ...prevState.storedUserTags, tag
          ]
        }))
      }
    }
  }



  // create a tag list that contains all tags for this doctor
  let tagList = allTags.currentTags.map((tag) => {
    return (
      <Fragment>
        <li key={tag.tagName}>
          <Chip
            label={tag.tagName + ' (' + tag.count + ')'}
            className={classes.tag}
            color="secondary"
            size="small"
          />
        </li>
      </Fragment>
    )
  });

  // if (props.searchInfo) {
  //   console.log(props.searchInfo.tags)
  // }

  console.log(allTags.currentTags)

  return (
    <div>
      <Hidden smUp>
        <hr className={classes.line}></hr>
        <Box
          mt={2}
          mb={2}
          display="flex"
          flexDirection="column"
          className={classes.reviewTagsBox}
        >
          <Typography variant="h6" color="primary">
            Review Tags
          </Typography>
          <Divider className={classes.divider} />
        </Box>
      </Hidden>
      {/* 显示这个doctor现在有的tags, 目前是hardcoded的， 麻烦你了 */}
      <div className={classes.tagDialog}>
        {tagList}{" "}
        {/* 如果登入了，edit tags的button会出现，否则不会出现 */}
        {authenticated ? (
          <Button
            color="primary"
            startIcon={<EditOutlinedIcon />}
            onClick={handleClickOpen}
            style={{ textTransform: "none" }}
            className={classes.editTagsButton}
          >
            Edit Tags
          </Button>
        ) : (
            ""
          )}
      </div>

      <Dialog onClose={handleClose} open={open} maxWidth="xs">
        <Typography variant="Body" style={{ margin: 20 }}>
          Please select tag(s) that best describe this doctor from the list
          below:
        </Typography>
        {/* 弹窗出现所有tags选项 */}
        <div className={classes.editTagDialog}>
          {editTags.map((tag) => {

            // let index = props.tagInfo.findIndex(eachTag => eachTag.tagName == tag)
            // // if the 
            // let selected = (index > -1)? "true" : "false";



            return (
              <Fragment>
                <PreDefinedTag tag={tag} addRemoveTag={addRemoveTag}/>
              </Fragment>
            );
          })}
        </div>

        {/* Done button， 目前是点了就关掉窗口，没有save user点了什么 */}
        <DialogActions>
          <Button
            onClick={handleClose}
            variant="outlined"
            color="primary"
            style={{ textTransform: "none", margin: 10 }}
          >
            Done
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}

function PreDefinedTag(props) {
  const classes = useStyles();

  const [selected, selectUnselectTag] = React.useState(false);

  const handleClick = () => {
    selectUnselectTag(!selected);
    props.addRemoveTag(props.tag.label);
  };

  return (
    <li key={props.tag.key}>
      <Chip
        label={props.tag.label}
        className={classes.tag}
        color="secondary"
        variant={selected ? "default" : "secondary"}
        onClick={handleClick}
      />
    </li>
  )
}


Tags.propTypes = {
  changeProfilePic: PropTypes.func.isRequired,
};

const mapStateToProps = (state) => ({
  // user: state.user,
  authenticated: state.user.authenticated,
  storedCredentials: state.user.credentials,
  searchInfo: state.data.searchInfo
});

const mapActionsToProps = {
  // changeProfilePic,
};

export default connect(mapStateToProps, mapActionsToProps)(Tags);
