import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import Box from "@material-ui/core/Box";
import { Typography } from "@material-ui/core";
import aboutBg from "../../img/home/aboutBg.png";
import Divider from "@material-ui/core/Divider";
import Button from "@material-ui/core/Button";
import Paper from "@material-ui/core/Paper";

const useStyles = makeStyles((theme) => ({
  container: {
    marginTop: 120,
  },
  root: {
    flexGrow: 1,
  },
  grid: {
    marginTop: 30,
  },
  divider: {
    marginTop: 20,
    height: 6,
    width: 80,
    backgroundColor: "#FF8686",
    justifyContent: "center",
  },
  aboutHeading: {
    color: "#fff",

    [theme.breakpoints.down("sm")]: {
      fontSize: 24,
    },
    fontSize: 28,
  },

  doctorsImg: {
    marginLeft: 20,
    marginRight: 20,
    width: "90%",
    height: "90%",
    borderRadius: 12,
    [theme.breakpoints.down("sm")]: {
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
    },
  },

  aboutTitle: {
    marginRight: 40,
    marginLeft: 40,

    [theme.breakpoints.down("sm")]: {
      fontSize: 20,
    },
  },

  aboutBody: {
    marginRight: 60,
    lineHeight: 2,
    marginLeft: 40,
    color: "white",
  },
  highlightText: {
    color: "#FF8686",
  },

  aboutPaper: {
    backgroundImage: `url(${aboutBg})`,
    backgroundPosition: "center center",
    backgroundSize: "cover",
    height: "100vh",
  },
}));

export default function About() {
  const classes = useStyles();

  return (
    <div className={classes.container}>
      <Paper className={classes.aboutPaper} elevation={0} square>
        <Box
          display="flex"
          flexDirection="column"
          alignItems="center"
          justifyContent="center"
        >
          <Divider className={classes.divider} />
          <br></br>
          <Typography
            color="primary"
            align="center"
            className={classes.aboutHeading}
          >
            About <span className={classes.highlightText}>MY</span>Doc
          </Typography>
          <br></br>
        </Box>
        <Typography align="center" variant="body" className={classes.aboutBody}>
          MyDoc is a comprehensive doctor information platform aims at helping
          people in Malaysia to search for doctor information related to their
          unique medical needs efficiently.
          <br></br>
          We hope MyDoc will be a new way for people to recommend doctor and
          make doctors more discoverable for people seeking their services.
        </Typography>
      </Paper>
    </div>
  );
}
