import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import Box from "@material-ui/core/Box";
import Typography from "@material-ui/core/Typography";
import Divider from "@material-ui/core/Divider";

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    backgroundColor: "#D3EAF0",
  },

  divider: {
    marginTop: 60,
    height: 6,
    width: 80,
    backgroundColor: "#FF8686",
    justifyContent: "center",
  },

  aboutHeading: {
    [theme.breakpoints.down("sm")]: {
      fontSize: 24,
    },
  },

  doctorsImg: {
    marginLeft: 20,
    marginRight: 20,
    width: "90%",
    height: "90%",
    borderRadius: 12,
    [theme.breakpoints.down("sm")]: {
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
    },
  },

  aboutBody: {
    //marginRight: 40,
    lineHeight: 2,
    //marginLeft: 40,

    color: "rgba(0, 0,0, 0.6)",
  },

  highlightText: {
    color: "#FF8686",
  },

  aboutText: {
    marginTop: 20,
    marginLeft: 80,
    marginRight: 80,
    [theme.breakpoints.down("xs")]: {
      marginLeft: 10,
      marginRight: 10,
    },
  },
}));

export default function About() {
  const classes = useStyles();

  return (
    <div style={{ marginTop: 120 }}>
      <div className={classes.root}>
        <Grid container spacing={0} style={{ marginTop: 30 }}>
          <Grid item xs={12}>
            <Box
              display="flex"
              flexDirection="column"
              alignItems="center"
              justifyContent="center"
            >
              <Divider className={classes.divider} />
              <br></br>
              <Typography
                color="primary"
                align="center"
                style={{ fontSize: 28 }}
                className={classes.aboutHeading}
              >
                About <span className={classes.highlightText}>MY</span>Doc
              </Typography>
              <br></br>
            </Box>
            <Box
              mb={8}
              mt={3}
              ml={12}
              mr={12}
              display="flex"
              flexDirection="column"
            >
              <Typography variant="body" className={classes.aboutBody}>
                MyDoc is a comprehensive doctor information platform aims at
                helping people in Malaysia to search for doctor information
                related to their unique medical needs efficiently.
              </Typography>
              <br></br>
              <Typography variant="body" className={classes.aboutBody}>
                We hope MyDoc will be a new way for people to recommend doctor
                and make doctors more discoverable for people seeking their
                services.
              </Typography>
            </Box>
          </Grid>
        </Grid>
      </div>
    </div>
  );
}
