import React, { Fragment } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import headerBg from "../../img/home/header_bg.png";
import Box from "@material-ui/core/Box";
import Paper from "@material-ui/core/Paper";
import headerImg from "../../img/home/doctors-heart.png";
import { Typography } from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },

  grid: {
    position: "relative",
  },
  headerImg: {
    width: "30%",
    position: "absolute",
    right: 0,
    marginTop: 150,
    marginRight: 50,
  },

  headerBg: {
    width: "100%",
  },

  paper: {
    backgroundImage: `url(${headerBg})`,
    backgroundRepeat: "no-repeat",
    backgroundPosition: "center center",
    backgroundSize: "100% 100%",
    height: "100vh",
  },

  title: {
    color: "#fff",
  },

  headings: {
    marginTop: 200,
    marginLeft: 50,
  },

  /* leftGrid: {
    marginTop: 200,
    marginLeft: 30,
    //margin: "auto",
  },

  rightGrid: {
    marginTop: 80,
  }, */
}));
/*
const headerBackground = {
  backgroundImage: "url(" + headerBg + ")",
}; */
export default function Header() {
  const classes = useStyles();
  //className={classes.paper}
  /*<img
  src={headerBg}
  alt="doctor-hearts"
  className={classes.headerBg}
/> */

  return (
    <div>
      <Paper elevation={0} className={classes.paper}>
        <div className={classes.root}>
          <Grid
            container
            spacing={0}
            className={classes.grid}
            //direction="column"
            //alignItems="center"
            justify="center"
          >
            <Grid item xs={8} className={classes.leftGrid}>
              <div className={classes.headings}>
                <Typography variant="h3" className={classes.title}>
                  Find your Ideal Doctor
                </Typography>
                <br></br>
                <Typography variant="h6" className={classes.title}>
                  Quick access to doctor information in Malaysia
                </Typography>
              </div>
            </Grid>
            <Grid item xs={4} className={classes.rightGrid}>
              <img
                src={headerImg}
                alt="doctor-hearts"
                className={classes.headerImg}
              />
            </Grid>
          </Grid>
        </div>
      </Paper>
    </div>
  );
}
