import React, { Fragment } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Drawer from "@material-ui/core/Drawer";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import Box from "@material-ui/core/Box";
import Grid from "@material-ui/core/Grid";
import Pagination from "@material-ui/lab/Pagination";
import ToggleButton from "@material-ui/lab/ToggleButton";
import ToggleButtonGroup from "@material-ui/lab/ToggleButtonGroup";
import Hidden from "@material-ui/core/Hidden";

// components
import CovidAlert from "../Alert";
import DocCard from "./DocCard";
import HospitalCard from "./HospitalCard";
import {
  HospitalType,
  Languages,
  YearsOfPractice,
  Location,
} from "./FiltersFunctions";
import { FilterButtonPhone } from "./FiltersUI_Phone";

import { withStyles } from "@material-ui/core/styles";

// const useStyles = makeStyles((theme) => ({
//   root: {
//     display: "flex",
//     height: "100vh",
//   },
//   appBar: {
//     zIndex: theme.zIndex.drawer + 1,
//   },
//   drawer: {
//     width: 240,
//     flexShrink: 0,
//   },
//   drawerPaper: {
//     width: 240,
//   },
//   drawerContainer: {
//     overflow: "auto",
//   },

//   content: {
//     flexGrow: 1,
//     padding: theme.spacing(3),
//   },

//   toggleContainer: {
//     margin: theme.spacing(3, 0),
//   },
// }));

const styles = (theme) => ({
  root: {
    display: "flex",
    height: "100vh",
  },
  appBar: {
    zIndex: theme.zIndex.drawer + 1,
  },
  drawer: {
    width: 240,
    flexShrink: 0,
  },
  drawerPaper: {
    width: 240,
  },
  drawerContainer: {
    overflow: "auto",
  },

  content: {
    flexGrow: 1,
    padding: theme.spacing(3),
  },

  toggleContainer: {
    margin: theme.spacing(3, 0),
  },
});

class SearchResultsFilter extends React.Component {
  state = {
    display: "doctor",
    setDisplay: "doctor",
  };

  handleDisplay = (event, newDisplay) => {
    if (newDisplay != null) {
      this.setState(setDisplay(newDisplay));
    }
    // this.setState({ setDisplay: event.target.value });
  };

  render() {
    const { classes } = this.props;

    return (
      <Fragment>
        <Hidden smDown>
          <div className={classes.root}>
            <Drawer
              className={classes.drawer}
              variant="permanent"
              classes={{
                paper: classes.drawerPaper,
              }}
            >
              <Toolbar />
              {/* For filter sidebar */}
              <div className={classes.drawerContainer}>
                <Box
                  display="flex"
                  flexDirection="column"
                  justifyContent="center"
                  ml={8}
                >
                  <br></br>
                  <br></br>
                  <HospitalType />
                  <br></br>
                  <br></br>
                  <Languages />
                  <br></br>
                  <br></br>
                  {/* If display by doctor, filter sidebar will show years of practice; 
            if display by hospital, filter sidebar will show location */}
                  {this.state.display === "doctor" ? (
                    <YearsOfPractice />
                  ) : (
                    <Location />
                  )}
                </Box>
              </div>
            </Drawer>
            <main className={classes.content}>
              <Toolbar />
              {/* Covid Alert */}
              <div
                className={classes.covidBox}
                style={{ marginLeft: 30, marginRight: 30 }}
              >
                <CovidAlert />
              </div>
              {/* Search result content starts here */}
              <Grid container spacing={0}>
                <Grid item sm={9} lg={10}>
                  {/* if user clicks on 'doctor' button, then render 'display results by doctors; else 'display results by hospitals'*/}
                  {this.state.display === "doctor" ? (
                    <Typography
                      style={{
                        marginLeft: 30,
                        marginTop: 30,
                        marginBottom: 30,
                      }}
                      variant="h6"
                      color="primary"
                    >
                      Display results by doctors
                    </Typography>
                  ) : (
                    <Typography
                      style={{
                        marginLeft: 30,
                        marginTop: 30,
                        marginBottom: 30,
                      }}
                      variant="h6"
                      color="primary"
                    >
                      Display results by hospitals
                    </Typography>
                  )}
                </Grid>
                <Grid item sm={3} lg={2}>
                  {/* Display by Doctor/Hospital buttons */}
                  <Fragment>
                    <div className={classes.toggleContainer}>
                      <ToggleButtonGroup
                        value={this.state.display}
                        exclusive
                        onChange={this.handleDisplay}
                      >
                        <ToggleButton value="doctor" color="primary">
                          <Typography
                            color="primary"
                            style={{ textTransform: "none" }}
                          >
                            Doctor
                          </Typography>
                        </ToggleButton>
                        <ToggleButton value="hospital">
                          <Typography
                            color="primary"
                            style={{ textTransform: "none" }}
                          >
                            Hospital
                          </Typography>
                        </ToggleButton>
                      </ToggleButtonGroup>
                    </div>
                  </Fragment>
                </Grid>
              </Grid>
              <div>
                {/* if user clicks on display by 'doctor', then render doctor cards */}
                {this.state.display === "doctor" ? (
                  <Fragment>
                    <DocCard />
                    <DocCard />
                    <DocCard />
                    <DocCard />
                    <DocCard />
                    <DocCard />
                    <DocCard />
                    <DocCard />
                  </Fragment>
                ) : (
                  <Fragment>
                    <HospitalCard />
                    <HospitalCard />
                    <HospitalCard />
                  </Fragment>
                )}
              </div>
            </main>
          </div>
        </Hidden>
      </Fragment>
    );
  }
}
export default withStyles(styles)(SearchResultsFilter);

