import {
  SET_USER,
  SET_AUTHENTICATED,
  SET_UNAUTHENTICATED,
  LOADING_USER,
} from "../types";

// not logged in!
const initialState = {
  authenticated: false,
  loading: false,
  credentials: {},
  //likes: [],
};

//the action that we receive
//depending on action type, we will do something
export default function (state = initialState, action) {
  switch (action.type) {
    /*catch our type */
    /* if we get set_authenticated (call from App.js) spread the state we already have, make authenticated: true*/
    case SET_AUTHENTICATED:
      return {
        ...state,
        authenticated: true,
      };
    /*when we logout, return to initial state*/
    case SET_UNAUTHENTICATED:
      return initialState;

    case SET_USER:
      return {
        authenticated: true,
        loading: false, //7:02
        ...action.payload,
      };
    //7:02
    case LOADING_USER:
      return {
        ...state,
        loading: true,
      };

    default:
      return state;
  }
}
