// 以后会用到
import {
    // LOADING_USER,
    GET_DATA
} from "../types";
import axios from "axios";


// used in App.js to fetch user data
export const getSearchInfo = () => (dispatch) => {
    axios
        .get("/searchinfo")
        .then((res) => {
            dispatch({
                type: GET_DATA /* use in userReducer.js */,
                payload: res.data,
            });
        })
        .catch((err) => console.log(err));
};

// used in DocTags.js
export const updateDoctorTags = (doctorTags) => (dispatch) => {
    // dispatch({ type: LOADING_USER });
    axios
        .post("/updatedoctortags", doctorTags)
        //   .then(() => {
        //     dispatch(getUserData());
        //   })
        .catch((err) => console.log(err));
};
