import React, { Fragment, useEffect } from "react";
import { connect } from "react-redux";
import { makeStyles } from "@material-ui/core/styles";

import Dialog from "@material-ui/core/Dialog";
import Chip from "@material-ui/core/Chip";
import Button from "@material-ui/core/Button";
import Box from "@material-ui/core/Box";
import Typography from "@material-ui/core/Typography";
import DialogActions from "@material-ui/core/DialogActions";
import Hidden from "@material-ui/core/Hidden";
import Divider from "@material-ui/core/Divider";
import EditOutlinedIcon from "@material-ui/icons/EditOutlined";

import PropTypes from "prop-types";


const useStyles = makeStyles((theme) => ({
  editTagDialog: {
    display: "flex",
    justifyContent: "center",
    flexWrap: "wrap",
    listStyle: "none",
    padding: theme.spacing(0.5),
    margin: 0,
  },

  tag: {
    margin: theme.spacing(0.5),
  },

  tagDialog: {
    display: "flex",
    flexWrap: "wrap",
    listStyle: "none",
    padding: theme.spacing(0.5),
    margin: 0,
    [theme.breakpoints.down("xs")]: {
      marginLeft: 10,
    },
    marginBottom: 20,
  },

  editTagsButton: {
    [theme.breakpoints.down("xs")]: {
      marginLeft: 20,
    },
  },

  line: {
    [theme.breakpoints.down("xs")]: {
      border: "5px solid rgba(0, 0, 0, 0.05)",
    },
  },

  reviewTagsBox: {
    [theme.breakpoints.down("xs")]: {
      justifyContent: "left",
      alignItems: "left",
      marginLeft: 20,
    },
  },

  divider: {
    height: 2,
    width: 115,
    backgroundColor: "#FF8686",
  },
}));

// Edit Hospital Tags (用在HospInfo.js)
// 目前hospital所有的tags and edit tags 都是hardcoded， 麻烦你了
function HospTags(props) {
  const classes = useStyles();
  const authenticated = props.authenticated;

  const [allTags, setState] = React.useState({
    currentTags: [],
    storedUserTags: []
  })

  const [renderCount, setRenderCount] = React.useState(0);

  // only call useEffect if renderCount = 0 (will be updated to 1 if stored data
  // in firebase is successfully set to state)
  useEffect(() => {
    if (renderCount == 0) {
      return displayStoredData();
    }
  }, [allTags]);

  const displayStoredData = () => {
    // call function to get data from returned props from firebase
    getStoredData()
      .then((res) => {
        // set state
        let userStored;
        if (!res[1].addedHospitalTags) {
          userStored = "";
        } else {
          if (!res[1].addedHospitalTags.PantaiHospitalKualaLumpur) {
            userStored = "";
          } else {
            userStored = res[1].addedHospitalTags.PantaiHospitalKualaLumpur;
          }
        }
        setState({
          currentTags: res[0].tags ? res[0].tags : "",
          storedUserTags: userStored
        })
        // update renderCount to 1 to stop react from making any more useEffect call
        setRenderCount(1);
      }).catch((error) => {
        console.error(error);
      })
  }

  // wait for returned props from firebase to be ready
  let getStoredData = async () => {
    // making two asynchronous calls: one from searchInfo and one from user credentials info
    let [storedSearchInfo, userStoredCredentials] =
      await Promise.all([props.hospSearchInfo, props.storedCredentials]);
    return [storedSearchInfo, userStoredCredentials];
  }



  // 这是点进去edit tags 会出现的tags选项
  const [editTags, displayEditedTags] = React.useState([
    { key: 0, label: "Clean" },
    { key: 1, label: "Customer Service" },
    { key: 2, label: "Modern" },
    { key: 3, label: "Organized " },
    { key: 4, label: "Reliable" },
    { key: 5, label: "Spacious " },
    { key: 6, label: "Welcoming" },
    { key: 7, label: "Well-equipped" },
  ]);

  const [open, setOpen] = React.useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = (value) => {
    setOpen(false);
  };

  // 这里我想说点了一个tag，就会换色（从outlined 换成default)
  // 但是我不会写那个logic,所以目前是点了一个tag,所有tag都一起换色了。。麻烦你了
  // 还有需要弄一个logic是 user再点一次又会换成原来outlined的style,麻烦你了
  const [color, setColor] = React.useState("outlined");
  const handleClick = () => {
    setColor("default");
  };

  // create a tag list that contains all tags for this hospital
  let tagList = [];
  props.tagInfo.map((tag) => {
    tagList.push((
      <Fragment>
        <li key={tag.TagName}>
          <Chip
            label={tag.TagName + ' (' + tag.Number + ')'}
            className={classes.tag}
            color="secondary"
            size="small"
          />
        </li>
      </Fragment>
    ));
  })

  return (
    <div>
      <Hidden smUp>
        <hr className={classes.line}></hr>
        <Box
          mt={2}
          mb={2}
          display="flex"
          flexDirection="column"
          className={classes.reviewTagsBox}
        >
          <Typography variant="h6" color="primary">
            Review Tags
          </Typography>
          <Divider className={classes.divider} />
        </Box>
      </Hidden>
      {/* 显示这个hospital现在有的tags, 目前是hardcoded的， 麻烦你了 */}
      <div className={classes.tagDialog}>
        {tagList}{" "}
        {/* 如果登入了，edit tags的button会出现，否则不会出现 */}
        {authenticated ? (
          <Button
            color="primary"
            startIcon={<EditOutlinedIcon />}
            onClick={handleClickOpen}
            style={{ textTransform: "none" }}
            className={classes.editTagsButton}
          >
            Edit Tags
          </Button>
        ) : (
          ""
        )}
      </div>

      <Dialog onClose={handleClose} open={open} maxWidth="xs">
        <Typography variant="Body" style={{ margin: 20 }}>
          Please select tag(s) that best describe this hospital from the list
          below:
        </Typography>
        {/* 弹窗出现所有tags选项 */}
        <div className={classes.editTagDialog}>
          {editTags.map((tag) => {
            return (
              <Fragment>
                <li key={tag.key}>
                  <Chip
                    label={tag.label}
                    className={classes.tag}
                    color="secondary"
                    variant={color}
                    onClick={handleClick}
                  />
                </li>
              </Fragment>
            );
          })}
        </div>

        {/* Done button， 目前是点了就关掉窗口，没有save user点了什么 */}
        <DialogActions>
          <Button
            onClick={handleClose}
            variant="outlined"
            color="primary"
            style={{ textTransform: "none", margin: 10 }}
          >
            Done
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}


HospTags.propTypes = {
  // updateUserStoredDocTags: PropTypes.func.isRequired,
};

const mapStateToProps = (state) => ({
  authenticated: state.user.authenticated,
  storedCredentials: state.user.credentials,
  hospSearchInfo: state.data.hospSearchInfo
});

const mapActionsToProps = {
  // updateUserStoredDocTags,
  // updateDoctorTags
};

export default connect(mapStateToProps, mapActionsToProps)(HospTags);
