import React, { Component, Fragment } from "react";
import IconButton from "@material-ui/core/IconButton";
import MenuItem from "@material-ui/core/MenuItem";
import Menu from "@material-ui/core/Menu";
import Avatar from "@material-ui/core/Avatar";
import { withStyles } from "@material-ui/core/styles";
import { logoutUser } from "../redux/actions/userActions";
import { connect } from "react-redux";

const styles = (theme) => ({
  ...theme.userMenu,
});

class UserMenu extends Component {
  state = {
    anchorEl: null,
    setAnchorEl: null,
  };

  handleLogout = () => {
    this.props.logoutUser();
  };

  handleMenu = (event) => {
    this.setState({ anchorEl: event.currentTarget });
  };

  handleClose = () => {
    this.setState({ anchorEl: null });
  };

  render() {
    const {
      classes,
      user: {
        credentials: { firstLetter },
      },
      userType,
    } = this.props;

    return (
      <Fragment>
        <IconButton onClick={this.handleMenu} className={classes.userButton}>
          <Avatar className={classes.avatar}>{firstLetter}</Avatar>
        </IconButton>
        <Menu
          id="menu-appbar"
          anchorEl={this.state.anchorEl}
          getContentAnchorEl={null}
          anchorOrigin={{
            vertical: "bottom",
            horizontal: "right",
          }}
          keepMounted
          transformOrigin={{
            vertical: "top",
            horizontal: "right",
          }}
          open={Boolean(this.state.anchorEl)}
          onClose={this.handleClose}
        >
          {userType === "general" ? (
            <Fragment>
              <MenuItem onClick={this.handleClose}>Saved</MenuItem>
              <MenuItem onClick={this.handleClose}>Like History</MenuItem>
              <MenuItem onClick={this.handleClose}>Account Settings</MenuItem>
              <MenuItem onClick={this.handleLogout}>Log out</MenuItem>
            </Fragment>
          ) : (
            <Fragment>
              <MenuItem onClick={this.handleClose}>Profile</MenuItem>
              <MenuItem onClick={this.handleClose}>Saved</MenuItem>
              <MenuItem onClick={this.handleClose}>Like History</MenuItem>
              <MenuItem onClick={this.handleClose}>
                Account Verification
              </MenuItem>
              <MenuItem onClick={this.handleClose}>Account Settings</MenuItem>
              <MenuItem onClick={this.handleLogout}>Log out</MenuItem>
            </Fragment>
          )}
        </Menu>
      </Fragment>
    );
  }
}
const mapStateToProps = (state) => ({
  user: state.user,
  userType: state.user.credentials.userType,
});

export default connect(mapStateToProps, { logoutUser })(
  withStyles(styles)(UserMenu)
);
