import {
    // LOADING_USER,
    GET_DATA
  } from "../types";
  import axios from "axios";


  // used in App.js to fetch user data
  export const getSearchInfo = () => (dispatch) => {
    // dispatch({ type: LOADING_USER });
    axios
      .get("/searchinfo")
      .then((res) => {
        dispatch({
          type: GET_DATA /* use in userReducer.js */,
          payload: res.data,
        });
      })
      .catch((err) => console.log(err));
  };

