sourcesContent:
// material ui theme
export default {
  typography: {
    fontFamily: '"Montserrat", sans-serif',
    h5: {
      fontFamily: "'Raleway', sans-serif",
    },
  },
  palette: {
    primary: {
      main: "#003367",
      contrastText: "#FFF",
    },
    secondary: {
      main: "#FF8686",
      contrastText: "#FFF",
    },
  },
  nav: {
    navButton: {
      textTransform: "none",
      position: "absolute",
      right: 0,
      marginRight: 20,
    },

    iconButton: {
      position: "absolute",
      right: 0,
      marginRight: 20,
    },
  },

  userMenu: {
    userButton: {
      position: "absolute",
      right: 0,
      marginRight: 20,
    },
    avatar: {
      backgroundColor: "#FF8686",
    },
  },
  //login + signup
  auth: {
    form: {
      textAlign: "left",
    },

    loginGrid: {
      margin: "auto",
    },

    pageTitle: {
      color: "#003367",
      fontWeight: "bold",
      marginTop: 20,
      marginBottom: 30,
      fontSize: "1.25em",
    },

    textField: {
      margin: "10px auto 10px auto",
    },
    button: {
      marginTop: 12,
      fontFamily: "'Montserrat', sans-serif",
      textTransform: "none",
      marginBottom: 30,
      position: "relative",
    },

    customError: {
      color: "red",
      fontSize: "0.8rem",
      marginTop: 10,
    },

    progress: {
      position: "absolute",
    },

    link: {
      fontWeight: "bold",
    },

    bg: {
      width: "300px",
      height: "300px",
      position: "fixed",
      margin: 0,
      padding: 0,
      right: -120,
      bottom: 0,
    },
  },
};
