import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import clsx from "clsx";
import Card from "@material-ui/core/Card";
import CardHeader from "@material-ui/core/CardHeader";
import CardContent from "@material-ui/core/CardContent";
import Collapse from "@material-ui/core/Collapse";
import Avatar from "@material-ui/core/Avatar";
import IconButton from "@material-ui/core/IconButton";
import Typography from "@material-ui/core/Typography";
import testingIcon from "../../img/covid/testingIcon.png";
import publichospscreening from "../../img/covid/publichospscreening.jpg";
import { CardMedia } from "@material-ui/core";

import ExpandMoreIcon from "@material-ui/icons/ExpandMore";
const useStyles = makeStyles((theme) => ({
  root: {
    width: "100%",
    marginBottom: 40,
  },
  cardHeader: {
    color: "#003367",
    fontFamily: "'Raleway', sans-serif",
  },

  body: {
    fontFamily: '"Montserrat", sans-serif',
  },

  subtitle: {
    fontFamily: '"Montserrat", sans-serif',
    color: "#003367",
  },

  expand: {
    transform: "rotate(0deg)",
    marginLeft: "auto",
    transition: theme.transitions.create("transform", {
      duration: theme.transitions.duration.shortest,
    }),
  },
  expandOpen: {
    transform: "rotate(180deg)",
  },

  image: {
    width: "100%",
    height: "auto",
  },
  img: {
    margin: "auto",
    display: "block",
    maxWidth: "100%",
    maxHeight: "100%",
  },
}));

export default function Testing() {
  const classes = useStyles();
  const [expanded, setExpanded] = React.useState(false);

  const handleExpandClick = () => {
    setExpanded(!expanded);
  };

  return (
    <Card className={classes.root}>
      <CardHeader
        avatar={
          <Avatar variant="rounded" alt="testingIcon" src={testingIcon} />
        }
        action={
          <IconButton
            className={clsx(classes.expand, {
              [classes.expandOpen]: expanded,
            })}
            onClick={handleExpandClick}
            aria-expanded={expanded}
            aria-label="show more"
          >
            <ExpandMoreIcon />
          </IconButton>
        }
        title="Testing for COVID-19"
        titleTypographyProps={{ variant: "h6" }}
        className={classes.cardHeader}
      />

      <CardContent>
        <Typography className={classes.subtitle}>
          <strong>Public hospitals</strong>
        </Typography>
        <br></br>

        <Typography className={classes.body}>
          There are 57 public hospitals with free screening services. In order
          to get tested for COVID-19 at public hospitals, there are certain
          requirements to be met beforehand, such as close contact and previous
          travel history.
        </Typography>
      </CardContent>

      <Collapse in={expanded} timeout="auto" unmountOnExit>
        <CardContent>
          <CardMedia className={classes.image}>
            <img
              className={classes.img}
              alt="publichospscreening"
              src={publichospscreening}
            />
          </CardMedia>
        </CardContent>

        <br></br>
        <Typography className={classes.subtitle}>
          <strong>Private institutions</strong>
        </Typography>
        <br></br>

        <Typography className={classes.body}>
          You can also get tested at one of the private institutions/hospitals
          listed below with cost ranging from RM450 - RM700.
        </Typography>

        <Link
          href="https://www.facebook.com/BookDoc4u/photos/a.961497920580239/2883892761674069/?type=3&theater"
          onClick={preventDefault}
        >
          BookDoc
        </Link>
      </Collapse>
    </Card>
  );
}
