import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import Hidden from "@material-ui/core/Hidden";

//components from docAccount folder
import DocSideNav from "../Components/docAccount/DocSideNav";
import Navbar from "../Components/Navbar";
import CovidAlert from "../Components/Alert";

// import DocEditProfile from "../Components/docAccount/DocEditProfile";
import DocAccountVerification from "../Components/docAccount/DocAccountVerification";
import AccountSettings from "../Components/docAccount/AccountSettings";
import DocEditProfile from "../Components/docAccount/DocEditProfile";

//eshin加的 5/4/2020
import LikeHistorySaved from "../Components/docAccount/LikeHistorySaved";



// material ui style
const useStyles = makeStyles((theme) => ({
  covidBox: {
    marginTop: 64,
    [theme.breakpoints.down("sm")]: {
      marginTop: 20,
      marginLeft: 20,
      marginRight: 20,
    },
  },
}));


function DocAccount(props) {
  const classes = useStyles();

  const [credentials, setCredentials] = React.useState();


  // right panel on large screen
  let rightPanel;
  // what will be render on mobile screen
  let mobileScreen;

  const index = props.index;
  if (index == 0) {
    rightPanel = <DocEditProfile />;
    mobileScreen = <DocSideNav />;
  } else if (index == 1) {
    rightPanel = <DocEditProfile />;
    mobileScreen = <DocEditProfile />;
  } else if (index == 2) {
    rightPanel = <LikeHistorySaved saveLike="saved" />;
    mobileScreen = <LikeHistorySaved saveLike="saved" />;
  } else if (index == 3) {
    rightPanel = <LikeHistorySaved saveLike="likeHistory" />;
    mobileScreen = <LikeHistorySaved saveLike="likeHistory" />;
  } else if (index == 4) {
    rightPanel = <DocAccountVerification />;
    mobileScreen = <DocAccountVerification />;
  } else {
    rightPanel = <AccountSettings />;
    mobileScreen = <AccountSettings />;
  }

  useEffect(() => {
    return displayStoredData();
  }, []);



  const displayStoredData = () => {
    getStoredCredentials()
      .then((res) => {

        let credentials = returnedInfo[0].data.credentials;
        let username = credentials.username;



        setState({
          userType: credentials.userType,
          profilePic: credentials.imgSrc,
          tags: storedTags,
          username: username,
          likes: likes
        })

        setRenderCount(1);
      }).catch((error) => {
        console.error(error);
      })
  }


  let getStoredCredentials = async () => {
    let storedCredentials = await axios.get(proxyurl + axios.defaults.baseURL + "user");
    return storedCredentials;
  }













  return (
    <div>
      <Navbar />
      <Hidden smDown>
        <div className={classes.covidBox} style={{ position: "relative" }}>
          <CovidAlert />

          <Grid container spacing={0}>
            <Grid item md={4} lg={3}>
              <DocSideNav />
            </Grid>
            <Grid item sm={12} md={8} lg={9}>
              {/* default large screen shows profile even if profile is not selected */}
              {rightPanel}
            </Grid>
          </Grid>
        </div>
      </Hidden>
      <Hidden mdUp>{mobileScreen}</Hidden>
    </div>
  );
}

export default DocAccount;
