//images
import hospImg from "../../img/results/pantaihospital.png";

//components
import CovidAlert from "../Alert";
import HospTags from "./HospTags";

import PropTypes from "prop-types";

import { changeHospLikeStatus } from "../../redux/actions/userActions";
import { updateHospitalLikes } from "../../redux/actions/dataActions";


