function LikeHistory(props) {
  const classes = useStyles();

  const [display, setDisplay] = React.useState("doctor"); //display by doctor as default
  const handleDisplay = (event, newDisplay) => {
    if (newDisplay != null) {
      setDisplay(newDisplay);
    }
  };

  const [renderCount, setRenderCount] = React.useState(0);

  const [likeHistory, setLikeHistory] = React.useState({
    doctors: [],
    hospitals: []
  })


  useEffect(() => {
    return displayStoredCredentials();
  }, []);


  const displayStoredCredentials = () => {
    getStoredCredentials()
      .then((res) => {
        let storedLikeHistory = res.data.credentials.likeHistory;
        let likedDoctors;
        let likedHospitals;

        // if the user has not never liked any doctors or hospitals
        if (!storedLikeHistory) {
          likedDoctors = [];
          likedHospitals = [];
        } else {
          likedDoctors = storedLikeHistory.doctors ? storedLikeHistory.doctors : [];
          likedHospitals = storedLikeHistory.hospitals ? storedLikeHistory.hospitals : [];
        }

        setLikeHistory({
          doctors: likedDoctors,
          hospitals: likedHospitals
        });

        // setRenderCount(1);
      }).catch((error) => {
        console.error(error);
      })
  }

  let getStoredCredentials = async () => {
    let userStoredCredentials = await axios.get(proxyurl + axios.defaults.baseURL + "user");
    return userStoredCredentials;
  }


  let getSpecialtyData = async (doctorSpecialty) => {
    let data =
      await axios.get(proxyurl + axios.defaults.baseURL + "getallsearchdata",
        {
          params: {
            specialty: doctorSpecialty
          }
        })
    return data;
  }



  // list of liked doctor cards on doctor tab
  let doctorCards = likeHistory.doctors.map(doctor => {
    return (
      <DocCard key={doctor.username} doctorInfo={doctor} getSpecialtyData={getSpecialtyData}/>
    )
  })

  // list of liked hospitals cards on hospital tab
  let hospitalCards = likeHistory.hospitals.map(hospital => {
    return (
      <HospitalCard key={hospital.name} hospitalInfo={hospital} getSpecialtyData={getSpecialtyData}/>
    )
  })





  return (
    <a id="likehistory" className={classes.anchor}>
      <Grid container spacing={0}>
        <Grid item xs={1}></Grid>
        <Grid item xs={10} md={8}>
          <Box display="flex" mt={4} mb={3} flexWrap="wrap" alignItems="center">
            <Box flexGrow={1} flexDirection="row" mb={1}>
              <Typography variant="h5" color="primary">
                {/* Back button, 手机屏幕才会出现 */}
                <Hidden mdUp>
                  {/* <IconButton> */}
                  <Link to="docaccount">
                    <ArrowBackIosIcon
                      className={classes.backIcon}
                      fontSize="small"
                    />
                  </Link>
                  {/* </IconButton> */}
                </Hidden>
                <strong>Like History</strong>
              </Typography>
            </Box>
            {/* Display by Doctor/Hospital buttons */}
            <Box>
              <ToggleButtonGroup
                value={display}
                exclusive
                onChange={handleDisplay}
              >
                <ToggleButton value="doctor" color="primary">
                  <Typography color="primary" style={{ textTransform: "none" }}>
                    Doctor
                  </Typography>
                </ToggleButton>
                <ToggleButton value="hospital">
                  <Typography color="primary" style={{ textTransform: "none" }}>
                    Hospital
                  </Typography>
                </ToggleButton>
              </ToggleButtonGroup>
            </Box>{" "}
          </Box>

          <br></br>
          <br></br>

          {/* if user clicks on display by 'doctor', then render doctor cards */}
          {display === "doctor" ? (
            <Fragment>{doctorCards}</Fragment>
          ) : (
              <Fragment>{hospitalCards}</Fragment>
            )}
        </Grid>
        <Grid item xs={1} md={3}></Grid>
      </Grid>
    </a>
  );
}
