import React, { Fragment, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemText from "@material-ui/core/ListItemText";
import Avatar from "@material-ui/core/Avatar";
import Box from "@material-ui/core/Box";
import Typography from "@material-ui/core/Typography";
import FavoriteIcon from "@material-ui/icons/Favorite";
import Chip from "@material-ui/core/Chip";
import FavoriteOutlinedIcon from "@material-ui/icons/FavoriteBorderOutlined";
import PersonIcon from "@material-ui/icons/PersonOutlined";
import BookmarkIcon from "@material-ui/icons/BookmarkBorderOutlined";
import VerifiedUserIcon from "@material-ui/icons/VerifiedUserOutlined";
import SettingsIcon from "@material-ui/icons/SettingsOutlined";
import ErrorOutlineOutlinedIcon from "@material-ui/icons/ErrorOutlineOutlined";
import Tooltip from "@material-ui/core/Tooltip";
import Badge from "@material-ui/core/Badge";
import AddAPhotoIcon from "@material-ui/icons/AddAPhoto";

import { connect } from "react-redux";
import PropTypes from "prop-types";

import { changeProfilePic } from "../../redux/actions/userActions";


const useStyles = makeStyles((theme) => ({
  root: {
    width: "100%",
    height: "100%",
    minHeight: "100vh",
    [theme.breakpoints.down("sm")]: {
      "& .MuiListItem-root": {
        borderBottom: "10px solid rgba(237, 235, 237, 0.4)",
        backgroundColor: "rgba(255, 255, 255, 100)",
      },
      backgroundColor: "rgba(237, 235, 237, 0.4)",
    },

    [theme.breakpoints.up("md")]: {
      borderRight: "1px solid rgba(237, 235, 237, 1)",
    },
  },

  img: {
    width: theme.spacing(12),
    height: theme.spacing(12),
    objectFit: "contain",
  },

  likeBox: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "center",
    alignItems: "center",
  },

  tag: {
    margin: theme.spacing(0.4),
  },

  tagBox: {
    display: "flex",
    justifyContent: "center",
    flexWrap: "wrap",
    listStyle: "none",
    padding: theme.spacing(0.5),
    [theme.breakpoints.down("xs")]: {
      marginLeft: 10,
    },
    marginBottom: 20,
  },
}));

//Doctor Account 左边的navigation
function DocSideNav(props) {
  const classes = useStyles();

  // get imgSrc from firebase user credentials prop
  const {
    storedCredentials: { imgSrc }
  } = props;


  // determine if useEffect will be called: don't call if renderCount = 1
  const [renderCount, setRenderCount] = React.useState(0);
  // initialie profilePic with firebase imgSrc
  const [profilePic, setProfilePic] = React.useState("");

  useEffect(() => {
    console.log(renderCount)
    if (renderCount == 0) {
      console.log("fetching data .......")
      return displayStoredData();
    }
  });

  const displayStoredData = () => {
    // call function to get data from returned props from firebase
    getStoredImg()
      .then((res) => {
        // set state
        console.log(res)
        setProfilePic(res.imgSrc)
        // update renderCount to 1 to stop react from making any more useEffect call
        setRenderCount(1);
      }).catch((error) => {
        console.error(error);
      })
  }

  // wait for returned props from firebase to be ready
  let getStoredImg = async () => {
    let storedCredentials = await props.storedCredentials;
    console.log(props.storedCredentials)
    return storedCredentials;
  }


  // reads new image and set state
  const uploadProfilePicture = (event) => {
    let reader = new FileReader();
    let uploaded = event.target.files[0];
    if (uploaded) {
      reader.readAsDataURL(uploaded);
    }
    reader.onload = () => {
      setProfilePic(reader.result);
    };
  };




  // wait for the newly uploaded image to be updated to state and post to firebase
  useEffect(() => {
    if (renderCount == 1) {
      console.log("changed, sending...")
      return postProfilePic();
    }
  }, [profilePic]);

  const postProfilePic = () => {
    waitProfileUpdate()
      .then((imgSrc) => {
        // console.log(imgSrc)
        const imgSrcVar = {
          imgSrc: imgSrc
        }
        props.changeProfilePic(imgSrcVar);
        // }
      }).catch((error) => {
        console.error(error);
      });
  }

  let waitProfileUpdate = async () => {
    let newProfilePic = await profilePic;
    return newProfilePic;
  }


  //选 list item (profile, saved, like history, acc verification, acc settings)
  const [selectedIndex, setSelectedIndex] = React.useState();

  const handleListItemClick = (event, index) => {
    setSelectedIndex(index);
  };

  // doctor tags
  const [tags, displayTags] = React.useState([
    { key: 0, label: "Attentive (10)" },
    { key: 1, label: "Bedside manner (10)" },
    { key: 2, label: "Empathetic (10)" },
    { key: 3, label: "Enthusiastic (10)" },
    { key: 4, label: "Friendly (10)" },
    { key: 5, label: "Honest (10)" },
    { key: 6, label: "Knowledgeable (10)" },
    { key: 7, label: "Passionate (10)" },
    { key: 8, label: "Patient (10)" },
    { key: 9, label: "Respectful (10)" },
    { key: 10, label: "Responsible (10)" },
    { key: 11, label: "Trustworthy (10)" },
  ]);

  //tooltip first time user instruction
