import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import Typography from "@material-ui/core/Typography";
import Chip from "@material-ui/core/Chip";
import FavoriteIcon from "@material-ui/icons/Favorite";
import Grid from "@material-ui/core/Grid";
import Box from "@material-ui/core/Box";
import Hidden from "@material-ui/core/Hidden";

import hospImg from "../../img/results/pantaihospital.png";

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
    margin: 30,
  },
  details: {
    display: "flex",
    flexDirection: "row",
  },
  img: {
    width: "80%",
    [theme.breakpoints.only("md")]: {
      width: "85%",
    },
  },
  imageGrid: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "center",
    alignItems: "center",
  },
}));

//This card is imported in SearchResult.js

// Each indiivdual hospital card
export default function HospitalCard() {
  const classes = useStyles();

  return (
    <Card className={classes.root}>
      <div className={classes.details}>
        <Grid container spacing={0}>
          {/* For screen > ipad size */}
          <Hidden smDown>
            <Grid item md={3} className={classes.imageGrid}>
              {/* hospital logo image */}
              <CardMedia
                component="img"
                className={classes.img}
                image={hospImg}
              ></CardMedia>
            </Grid>
          </Hidden>
          {/* For screen = ipad size */}
          <Hidden mdUp>
            <Grid item sm={3} className={classes.imageGrid}>
              {/* hospital logo image */}
              <Box
                display="flex"
                flexDirection="column"
                justifyContent="center"
                alignItems="center"
              >
                <CardMedia
                  component="img"
                  className={classes.img}
                  image={hospImg}
                ></CardMedia>
                <Box
                  mt={8}
                  mr={1}
                  display="flex"
                  flexDirection="column"
                  justifyContent="center"
                  alignItems="center"
                >
                  <FavoriteIcon style={{ color: "red" }} />
                  <Typography variant="body2" color="primary">
                    1234
                  </Typography>
                </Box>
              </Box>
            </Grid>
          </Hidden>

          <Grid item xs={7} sm={9} md={7}>
            <CardContent>
              {/* hospital details */}
              <Typography variant="h6" color="primary">
                Pantai Hospital Kuala Lumpur
              </Typography>
              <br></br>
              <Typography variant="subtitle1" color="textSecondary">
                <strong>Address: </strong>
                <span>
                  Block 8, Jalan Bukit Pantai, Bangsar, 59100 Kuala Lumpur,
                  Wilayah Persekutuan Kuala Lumpur
                </span>
              </Typography>
              <Typography variant="subtitle1" color="textSecondary">
                <strong>Phone: </strong>
                <span>+603-22960888</span>
              </Typography>

              <br></br>
              <Chip color="secondary" size="small" label="Private"></Chip>
            </CardContent>
          </Grid>
          <Hidden smDown>
            <Grid item md={2}>
              {/* like icon + number of likes */}
              <Box
                mt={3}
                mr={1}
                display="flex"
                flexDirection="column"
                justifyContent="center"
                alignItems="center"
              >
                <FavoriteIcon style={{ color: "red" }} />
                <Typography variant="body2" color="primary">
                  1234
                </Typography>
              </Box>
            </Grid>
          </Hidden>
        </Grid>
      </div>
    </Card>
  );
}
