// 以后会用到
import {
    LOADING_USER,
    GET_DOC_DATA,
    GET_HOSP_DATA,
    GET_DOC_ACCOUNT_DATA,
    GET_SEARCH_DATA
} from "../types";
import axios from "axios";


// used in App.js to fetch user data
export const getDocSearchInfo = () => (dispatch) => {
    axios
        .get("/docsearchinfo")
        .then((res) => {
            dispatch({
                type: GET_DOC_DATA /* use in userReducer.js */,
                payload: res.data,
            });
        })
        .catch((err) => console.log(err));
};

// used in DocTags.js
export const updateDoctorTags = (doctorTags) => (dispatch) => {
    axios
        .post("/updatedoctortags", doctorTags)
        .catch((err) => console.log(err));
};

// used in DocTags.js
export const updateDoctorLikes = (likes) => (dispatch) => {
    axios
        .post("/updatedoctorlikes", likes)
        .catch((err) => console.log(err));
};


// ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~

// used in App.js to fetch user data
export const getHospSearchInfo = () => (dispatch) => {
    axios
        .get("/hospsearchinfo")
        .then((res) => {
            dispatch({
                type: GET_HOSP_DATA /* use in userReducer.js */,
                payload: res.data,
            });
        })
        .catch((err) => console.log(err));
};

// used in HospTags.js
export const updateHospTags = (hospTags) => (dispatch) => {
    axios
        .post("/updatehospitaltags", hospTags)
        .catch((err) => console.log(err));
};

// used in HospTags.js
export const updateHospitalLikes = (likes) => (dispatch) => {
    axios
        .post("/updatehospitallikes", likes)
        .catch((err) => console.log(err));
};

// doctor getting own account info from seach data
export const getSpecProfile = () => (dispatch) => {
    dispatch({ type: LOADING_USER });
    axios
        .get("/getspecprofile")
        .then((res) => {
            dispatch({
                type: GET_DOC_ACCOUNT_DATA /* use in userReducer.js */,
                payload: res.data,
            });
        })
        .catch((err) => console.log(err));
};

export const getAllSearchData = (specialtyList) => (dispatch) => {

    axios
        .get("/getallsearchdata", specialtyList)
        .then((res) => {
            dispatch({
                type: GET_SEARCH_DATA /* use in userReducer.js */,
                payload: res.data,
            });
        })
        .catch((err) => console.log(err));
};
