import React, { Component, Fragment } from "react";
import IconButton from "@material-ui/core/IconButton";
import MenuItem from "@material-ui/core/MenuItem";
import Menu from "@material-ui/core/Menu";
import Avatar from "@material-ui/core/Avatar";
//import { makeStyles } from "@material-ui/core/styles";
import { withStyles } from "@material-ui/core/styles";
import { logoutUser } from "../redux/actions/userActions";

//redux
import { connect } from "react-redux";

const styles = (theme) => ({
  userButton: {
    position: "absolute",
    right: 0,
    marginRight: 20,
  },
  avatar: {
    backgroundColor: "#FF8686",
  },
});

class UserMenu extends Component {
  constructor() {
    super();
    this.state = {
      anchorEl: null,
      setAnchorEl: null,
    };
  }

  handleLogout = () => {
    this.props.logoutUser();
  };

  /*state = {
    anchorEl: null,
    setAnchorEl: null,
  }; */

  handleMenu = (event) => {
    this.setState({ anchorEl: event.currentTarget });
  };

  handleClose = () => {
    this.setState({ anchorEl: null });
  };

  render() {
    const {
      classes,
      user: {
        credentials: { username },
      },
    } = this.props;
    {
      console.log("username", this.props.user.credentials.username);
    }
    return (
      <Fragment>
        <IconButton onClick={this.handleMenu} className={classes.userButton}>
          <Avatar className={classes.avatar}>{username}</Avatar>
        </IconButton>
        <Menu
          id="menu-appbar"
          anchorEl={this.anchorEl}
          getContentAnchorEl={null}
          anchorOrigin={{
            vertical: "bottom",
            horizontal: "right",
          }}
          keepMounted
          transformOrigin={{
            vertical: "top",
            horizontal: "right",
          }}
          open={Boolean(this.anchorEl)}
          onClose={this.handleClose}
        >
          <MenuItem onClick={this.handleClose}>Saved</MenuItem>
          <MenuItem onClick={this.handleClose}>Like History</MenuItem>
          <MenuItem onClick={this.handleClose}>Account Settings</MenuItem>
          <MenuItem onClick={this.handleLogout}>Log out</MenuItem>
        </Menu>
      </Fragment>
    );
  }
}
const mapStateToProps = (state) => ({
  user: state.user,
});

export default connect(mapStateToProps, { logoutUser })(
  withStyles(styles)(UserMenu)
);
