import React, { Fragment } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import headerBg from "../../img/home/header_bg.png";
import Box from "@material-ui/core/Box";
import Paper from "@material-ui/core/Paper";
import solution1 from "../../img/home/solution1.png";
import solution2 from "../../img/home/solution2.png";
import solution3 from "../../img/home/solution3.png";
import linearrow1 from "../../img/home/linearrow1.png";
import linearrow2 from "../../img/home/linearrow2.png";

import { Typography } from "@material-ui/core";
import SearchTabs from "./SearchTabs";
import Location from "./Location";
import SearchIcon from "@material-ui/icons/Search";
import Button from "@material-ui/core/Button";
import IconButton from "@material-ui/core/IconButton";
import Divider from "@material-ui/core/Divider";

const useStyles = makeStyles((theme) => ({
  container: {
    marginTop: 120,
  },
  root: {
    flexGrow: 1,
  },

  grid: {
    marginTop: 30,
  },

  divider: {
    marginTop: 20,
    height: 6,
    width: 80,
    backgroundColor: "#FF8686",
    justifyContent: "center",
  },
  solutionHeading: {
    [theme.breakpoints.down("sm")]: {
      fontSize: 24,
    },
  },

  solutionImg: {
    marginLeft: 20,
    marginRight: 20,
    width: "20%",
    height: "20%",
  },

  solutionTitle: {
    marginRight: 40,
    marginLeft: 40,

    [theme.breakpoints.down("sm")]: {
      fontSize: 20,
    },
  },

  solutionBody: {
    marginRight: 40,
    lineHeight: 2,
    marginLeft: 40,
    color: "grey",
  },
  highlightText: {
    color: "#FF8686",
  },
}));

export default function Solution() {
  const classes = useStyles();

  return (
    <div className={classes.container}>
      <div className={classes.root}>
        <Box
          display="flex"
          flexDirection="column"
          alignItems="center"
          justifyContent="center"
        >
          <Divider className={classes.divider} />
          <br></br>
          <Typography
            variant="h4"
            color="primary"
            align="center"
            className={classes.solutionHeading}
          >
            Solution
          </Typography>
          <br></br>
        </Box>
        <Grid container spacing={0} className={classes.grid}>
          <Grid item xs={12} sm={3} className={classes.leftGrid}>
            <br></br>
            <Box
              display="flex"
              flexDirection="column"
              alignItems="center"
              justifyContent="center"
            >
              <img
                src={solution1}
                alt="solution1"
                className={classes.solutionImg}
              />
              <br></br>

              <Typography
                variant="body"
                align="center"
                className={classes.solutionBody}
              >
                Users discover doctors by specialty, condition, and location
              </Typography>
            </Box>
          </Grid>
          <Grid item xs={12} sm={1}>
            <img
              src={linearrow1}
              alt="linearrow1"
              className={classes.solutionImg}
            />
          </Grid>

          <Grid item xs={12} sm={3} className={classes.rightGrid}>
            <br></br>
            <Box
              display="flex"
              flexDirection="column"
              alignItems="center"
              justifyContent="center"
            >
              <img
                src={solution2}
                alt="solution2"
                className={classes.solutionImg}
              />
              <br></br>

              <Typography
                variant="body"
                align="center"
                className={classes.solutionBody}
              >
                We gather all doctor information in one place and show you
                people’s recommended doctors
              </Typography>
            </Box>
          </Grid>
          <Grid item xs={12} sm={1}>
            <img
              src={linearrow2}
              alt="linearrow2"
              className={classes.solutionImg}
            />
          </Grid>
          <Grid item xs={12} sm={3} className={classes.leftGrid}>
            <br></br>
            <Box
              display="flex"
              flexDirection="column"
              alignItems="center"
              justifyContent="center"
            >
              <img
                src={solution3}
                alt="solution3"
                className={classes.solutionImg}
              />
              <br></br>

              <Typography
                variant="body"
                align="center"
                className={classes.solutionBody}
              >
                Doctors present their services online and engage new patients
              </Typography>
            </Box>
          </Grid>
        </Grid>
      </div>
    </div>
  );
}
