import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import Box from "@material-ui/core/Box";
import yin from "../../img/home/yin.png";
import eshin from "../../img/home/eshin.png";
import he from "../../img/home/he.png";
import michelle from "../../img/home/michelle.png";

import { Typography } from "@material-ui/core";
import Divider from "@material-ui/core/Divider";

const useStyles = makeStyles((theme) => ({
  container: {
    marginTop: 120,
  },
  root: {
    flexGrow: 1,
  },

  grid: {
    marginTop: 30,
  },

  divider: {
    marginTop: 20,
    height: 6,
    width: 80,
    backgroundColor: "#FF8686",
    justifyContent: "center",
  },
  teamHeading: {
    [theme.breakpoints.down("sm")]: {
      fontSize: 24,
    },
    fontSize: 28,
  },

  teamImg: {
    marginLeft: 20,
    marginRight: 20,
    width: "60%",
    height: "60%",
  },

  teamTitle: {
    marginRight: 40,
    marginLeft: 40,

    [theme.breakpoints.down("sm")]: {
      fontSize: 20,
    },
  },

  teamBody: {
    marginRight: 60,
    lineHeight: 2,
    marginLeft: 60,
    color: "rgba(0, 0,0, 0.6)",
    marginBottom: 30,
    [theme.breakpoints.down("sm")]: {
      marginRight: 40,
      marginLeft: 30,
    },
  },
  highlightText: {
    color: "#FF8686",
  },

  lineArrow: {
    width: "20%",
    height: "20%",
  },
  lineArrow2: {
    width: "35%",
    height: "35%",
  },
}));

export default function Team() {
  const classes = useStyles();

  return (
    <div className={classes.container}>
      <div className={classes.root}>
        <Box
          display="flex"
          flexDirection="column"
          alignItems="center"
          justifyContent="center"
        >
          <Divider className={classes.divider} />
          <br></br>
          <Typography
            color="primary"
            align="center"
            className={classes.teamHeading}
          >
            Meet the Team
          </Typography>
          <br></br>
        </Box>
        <Grid container spacing={0} className={classes.grid}>
          <Grid item xs={12} sm={3} className={classes.leftGrid}>
            <br></br>
            <Box
              display="flex"
              flexDirection="column"
              alignItems="center"
              justifyContent="center"
            >
              <img src={he} alt="he" className={classes.teamImg} />

              <br></br>

              <Typography
                variant="body"
                align="center"
                className={classes.teamBody}
              >
                He Chen{" "}
              </Typography>
            </Box>
          </Grid>

          <Grid item xs={12} sm={3} className={classes.rightGrid}>
            <br></br>
            <Box
              display="flex"
              flexDirection="column"
              alignItems="center"
              justifyContent="center"
            >
              <img src={michelle} alt="michelle" className={classes.teamImg} />

              <br></br>

              <Typography
                variant="body"
                align="center"
                className={classes.teamBody}
              >
                Michelle Chen
              </Typography>
            </Box>
          </Grid>

          <Grid item xs={12} sm={3} className={classes.leftGrid}>
            <br></br>
            <Box
              display="flex"
              flexDirection="column"
              alignItems="center"
              justifyContent="center"
            >
              <img src={yin} alt="yin" className={classes.teamImg} />

              <br></br>

              <Typography
                variant="body"
                align="center"
                className={classes.teamBody}
              >
                Yin Chen{" "}
              </Typography>
            </Box>
          </Grid>
          <Grid item xs={12} sm={3} className={classes.leftGrid}>
            <br></br>
            <Box
              display="flex"
              flexDirection="column"
              alignItems="center"
              justifyContent="center"
            >
              <img src={eshin} alt="eshin" className={classes.teamImg} />

              <br></br>

              <Typography
                variant="body"
                align="center"
                className={classes.teamBody}
              >
                Eshin Ang{" "}
              </Typography>
            </Box>
          </Grid>
        </Grid>
      </div>
    </div>
  );
}
