import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import Box from "@material-ui/core/Box";
import Typography from "@material-ui/core/Typography";

import SearchIcon from "@material-ui/icons/Search";
import Button from "@material-ui/core/Button";
import Hidden from "@material-ui/core/Hidden";

//components
import CovidAlert from "../Alert";
import SearchTabs from "./SearchTabs";
import Location from "./Location";

//image
import headerImg from "../../img/home/doctors-heart.png";

const useStyles = makeStyles((theme) => ({
  container: {
    marginTop: 64,
    [theme.breakpoints.down("sm")]: {
      marginTop: 2,
    },
    [theme.breakpoints.down("xs")]: {
      marginTop: 1,
    },
  },

  headerImg: {
    width: "100%",
    [theme.breakpoints.down("md")]: {
      width: "75%",
    },
    [theme.breakpoints.down("sm")]: {
      width: "95%",
    },
  },

  root: {
    display: "flex",
    flexDirection: "row",
    alignItems: "center",
    backgroundColor: "#003367",
  },

  title: {
    color: "white",
    fontWeight: "bold",
    [theme.breakpoints.down("xs")]: {
      fontSize: 24,
      //textAlign: "center",
    },
    [theme.breakpoints.down("sm")]: {
      fontSize: 26,
    },
  },
  body: {
    color: "white",
    textAlign: "center",
  },

  headings: {
    [theme.breakpoints.down("xs")]: {
      textAlign: "center",
    },
  },

  search: {
    marginTop: 20,
    height: 48,
    textTransform: "none",
    fontSize: 16,
  },

  locationBox: {
    [theme.breakpoints.up("md")]: {
      display: "flex",
      flexDirection: "row",
    },
  },

  rightGrid: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
  },
}));

export default function Header() {
  const classes = useStyles();

  return (
    <div className={classes.container}>
      <CovidAlert />

      <div className={classes.root}>
        <Grid container spacing={0}>
          <Grid item xs={1} sm={1} md={1} lg={2}></Grid>
          <Grid item xs={10} sm={5} md={5} lg={4}>
            <Box mt={16} mb={12} className={classes.box}>
              <div className={classes.headings}>
                <Typography
                  variant="h4"
                  style={{ color: "white", fontWeight: "bold" }}
                >
                  Find your Ideal Doctor
                </Typography>
                <br></br>
                <Typography variant="title" className={classes.body}>
                  Quick access to doctor information in Malaysia
                </Typography>
                <br></br>
                <br></br>
                <SearchTabs />
                <Location className={classes.location}></Location>
                <Button
                  variant="contained"
                  color="secondary"
                  size="md"
                  className={classes.search}
                  startIcon={<SearchIcon />}
                  fullWidth
                >
                  Search
                </Button>
              </div>
            </Box>
          </Grid>
          <Grid item sm={1} md={1} />

          <Grid item xs={1} sm={4} md={5} lg={3} className={classes.rightGrid}>
            <Hidden xsDown>
              <img
                src={headerImg}
                alt="doctor-hearts"
                className={classes.headerImg}
              />
            </Hidden>
          </Grid>
          <Grid item lg={2} />
        </Grid>
      </div>
      {/* </Paper> */}
    </div>
  );
}

