// Each indiivdual doctor card
function DocCard(props) {
  const classes = useStyles();
  // create language strings
  let language = "";
  props.doctorInfo["languages"].forEach((lang) => {
    language = language + ", " + lang;
  });
  language = language.substring(1);
  // const handleClick = () => {
  //   props.updateTargetDoc(props.resultData);
  //   if (props.history != null) {
  //     props.history.push("/docprofile");
  //   }
  // };

  return (
    // onClick={handleClick}
    <Card className={classes.root} >   
      <Grid container spacing={0}>
        <Grid item xs={12} sm={3} className={classes.imageGrid}>
          {/* doctor image */}
          <CardMedia
            component="img"
            className={classes.img}
            image={docImg}
          ></CardMedia>
        </Grid>
        <Grid item xs={12} sm={7}>
          <CardContent>
            {/* doctor details */}
            <Typography variant="h6" color="primary">
              {"Dr." + props.doctorInfo["name"]}
            </Typography>
            <br></br>
            <Typography variant="subtitle1" color="textSecondary">
              <strong>Specialty: </strong>{" "}
              <span>{props.doctorInfo["specialty"]}</span>
            </Typography>
            <Typography variant="subtitle1" color="textSecondary">
              <strong>Hospital: </strong>
              <span>{props.doctorInfo["hospital"]}</span>
            </Typography>
            <Typography variant="subtitle1" color="textSecondary">
              <strong>Languages: </strong>
              <span>{language}</span>
            </Typography>
            <br></br>
            {/* private tag */}
            <Chip color="secondary" size="small" label="Private"></Chip>
          </CardContent>
        </Grid>
        <Grid item xs={12} sm={2}>
          {/* Like icon + number of likes */}
          <Box className={classes.likeBox}>
            <FavoriteIcon style={{ color: "red" }} />
            <Typography variant="body2" color="primary">
              {props.doctorInfo["likes"]}
            </Typography>
          </Box>
        </Grid>
      </Grid>
    </Card>
  );
}


